ompt":true},"skippable":{"type":"Applicability","type":"Applicability","mode":"script","value":"false"},"id":"95d09506a92548158c1605814043dcd8","name":"Task Number","goal_id":"primary","applicability":{"type":"Applicability","type":"Applicability","mode":"script","value":"true"},"formatType":"Text","validationScript":""},{"type":"ScriptedAction","type":"ScriptedAction","script":"(function execute() {
    var addMessage = function(val) {
        if (message) {
            return message + '<br/>' + val;
        }
        return val;
    }
    var getGlideRecord = function(tableName) {
        return new GlideRecord(tableName);
    }
    var getDocument = function(number, documentTable) {
        var gr = getGlideRecord(documentTable);
        gr.addQuery('number', number);
        gr.query();
        return gr;
    }
    var getWorkItem = function(sysId) {
        var gr = getGlideRecord('awa_work_item');
        gr.addQuery('document_id', sysId);
        gr.query();
        return gr;
    }
    var getDocumentTable = function() {
        var id = vaInputs.task_number.toString();
        if (id.startsWith('INC')) {
            return 'incident';
        } else if (id.startsWith('PRB')) {
            return 'problem';
        } else {
            return null;
        }
    }
    var MSG_WORK_ITEM_GENERATED = '{0} work item generated. ';
    var MSG_WORK_ITEM_NOT_GENERATED = 'Work item is not created. ';
    var MSG_CHECK_FOR_BUSINESS_RULES = 'Check for Business rules on awa_work_item table or time out configuration based on the reason for canceling the work items. ';
    var MSG_NO_QUEUE_EXIST = 'No queue exist for Document with number: {0}. ';
    var MSG_DOCUMENT_NOT_FOUND = 'Document not found with number {0}. ';
    var MSG_AGENT_AVAILABLE = 'Right now Agents are available for Service Channel: {0}';
    var MSG_NO_AGENT_AVAILABLE = 'Right now no Agent available for Service Channel: {0}. ';
    var MSG_NO_AGENT_AVAILABLE_WITH_SKILLS = 'Right now no Agent available for Service Channel: {0} with required skills. ';
    var MSG_AGENT_AVAILABLE_WITH_SKILLS = 'Right now Agents are available for Service Channel: {0} with required skills. ';
    var MSG_AVAILABLE_QUEUES = 'Below queues are available. Please validate the schedule and utilization conditions: ';
    var MSG_WORK_ITEM_STATUS = 'Associated work item is in {0} state';
    var MSG_WORK_ITEM_STATUS_CANCELLED = 'Associated work item is in cancelled state with reason {0}';
    var message = '';
    var documentTable = getDocumentTable(vaInputs.task_number);
    if (!documentTable) {
        message = addMessage(gs.getMessage(MSG_DOCUMENT_NOT_FOUND, vaInputs.task_number));
        vaVars.output_message = message;
        return;
    }
    var documentGr = getDocument(vaInputs.task_number, documentTable);
    if (documentGr && documentGr.next()) {
        var workItemGr = getWorkItem(documentGr.getUniqueValue());
        var count = workItemGr.getRowCount();
        if (count > 1) {
            message = addMessage(gs.getMessage(MSG_WORK_ITEM_GENERATED, count));
            message = addMessage(gs.getMessage(MSG_CHECK_FOR_BUSINESS_RULES));
        } else if (count == 0) {
            message = addMessage(gs.getMessage(MSG_WORK_ITEM_NOT_GENERATED));
            var queueGr = new GlideRecord('awa_queue');
            queueGr.addEncodedQuery('active=true^service_channel.workitem_table=' + documentTable);
            queueGr.orderBy('order');
            queueGr.query();
            if (queueGr.getRowCount() == 0) {
                message = addMessage(gs.getMessage(MSG_NO_QUEUE_EXIST, vaInputs.task_number));
                vaVars.output_message = message;
                return;
            }
            message = addMessage(gs.getMessage(MSG_AVAILABLE_QUEUES));
            while (queueGr.next()) {
                var url = vaVars.base_url + 'awa_queue.do?sys_id=' + queueGr.getUniqueValue();
                message = addMessage('<a href=' + url + '>' + queueGr.getValue('name') + '</a>');
            }
        } else if (count == 1) {
            if (workItemGr.next()) {
                var state = workItemGr.getValue('state');
                if (state === 'cancelled') {
                    message = addMessage(gs.getMessage(MSG_WORK_ITEM_STATUS_CANCELLED, workItemGr.getValue('cancelled_reason')));
                } else {
                    message = addMessage(gs.getMessage(MSG_WORK_ITEM_STATUS, workItemGr.getValue('state')));
                }
                var queue = workItemGr.getValue('queue');
                var queueGr = new GlideRecord('awa_queue');
                queueGr.get(queue);
                var serviceChannel = queueGr.getValue('service_channel');
                var serviceChannelGr = new GlideRecord('awa_service_channel');
                serviceChannelGr.get(serviceChannel);
                var defaultCapacity = serviceChannelGr.getValue('default_capacity');
                var channelCapacityGr = new GlideRecord('awa_agent_presence_capacity');
                channelCapacityGr.addQuery('channel', serviceChannel);
                channelCapacityGr.addEncodedQuery('aca_presence_state.available_to_receive_work_items=true');
                channelCapacityGr.addQuery('ac_workload', '<', defaultCapacity);
                channelCapacityGr.query();
                if (channelCapacityGr.getRowCount() == 0) {
                    message = addMessage(gs.getMessage(MSG_NO_AGENT_AVAILABLE, serviceChannelGr.getValue('name')));
                    vaVars.output_message = message;
                    return;
                }
                var skillEnabled = gs.getProperty('glide.awa.skill_level.evaluate');
                if (skillEnabled && skillEnabled == 'true') {
                    var skills = [];
                    var skillGr = new GlideRecord('task_m2m_skill');
                    skillGr.addQuery('task', documentGr.getUniqueValue());
                    skillGr.query();
                    while (skillGr.next()) {
                        skills.push(skillGr.getUniqueValue());
                    }
                    skills.sort(function(a, b) {
                        return a.localeCompare(b);
                    });
                    while (channelCapacityGr.next()) {
                        channelCapacityGr.getValue('ap_agent');
                        var userSkillGr = new GlideRecord('sys_user_has_skill');
                        userSkillGr.addQuery('user', channelCapacityGr.getValue('ap_agent'));
                        userSkillGr.addQuery('skill', 'IN', skills.toString().replaceAll('[').replaceAll(']'));
                        userSkillGr.query();
                        if (userSkillGr.getRowCount() != skills.length) {
                            continue;
                        }
                        var userSkills = [];
                        while (userSkillGr.next()) {
                            userSkills.push(userSkillGr.getValue('skill'));
                        }
                        userSkills.sort(function(a, b) {
                            return a.localeCompare(b);
                        });
                        if (userSkills.toString() == skills.toString()) {
                            message = addMessage(gs.getMessage(MSG_AGENT_AVAILABLE_WITH_SKILLS, serviceChannelGr.getValue('name')));
                            vaVars.output_message = message;
                            return;
                        }
                    }
                    message = addMessage(gs.getMessage(MSG_NO_AGENT_AVAILABLE_WITH_SKILLS, serviceChannelGr.getValue('name')));
                } else {
                    message = addMessage(gs.getMessage(MSG_AGENT_AVAILABLE, serviceChannelGr.getValue('name')));
                }
            }
        }
    } else {
        message = addMessage(gs.getMessage(MSG_DOCUMENT_NOT_FOUND, vaInputs.task_number));
    }
    vaVars.output_message = message;
})()","id":"a3d5c445ab094cdcb32250ad964620ac","name":"evaluate","goal_id":"primary","applicability":{"type":"Applicability","type":"Applicability","mode":"script","value":"true"}}],"edges":[{"type":"Edge","source_node_id":"7b1fe139422244f1976ecfde3ad428fc","target_node_id":"3bbf2e8af7a94cea8af9fe2ee06aa232","branch_id":"f8e1ab71366e4c6fb60ca93927ed5140","id":"6efe10730c25433e99c9d15e6444c562","name":"f8e1ab71366e4c6fb60ca93927ed5140"},{"type":"Edge","source_node_id":"cc62e227f6dd4804aeaf3dbe74f259f4","target_node_id":"ca63c928a8864ffc8ff6704efbbc2517","id":"420ce22b44f741ee8f14e704cd22be0b"},{"type":"Edge","source_node_id":"975a0f59aa104801b0f3928fbad8ae54","target_node_id":"cc62e227f6dd4804aeaf3dbe74f259f4","id":"47e7cde6f1284af1a357eb00c61ada1d"},{"type":"Edge","source_node_id":"ca63c928a8864ffc8ff6704efbbc2517","target_node_id":"983ea6a2e3cb41d887dcdf851b07b6b7","id":"cc7181616fb947bba13be1c1dfc00440"},{"type":"Edge","source_node_id":"983ea6a2e3cb41d887dcdf851b07b6b7","target_node_id":"7b1fe139422244f1976ecfde3ad428fc","id":"33774829ee5e43bab4f69a5197c8c95b"},{"type":"Edge","source_node_id":"7b1fe139422244f1976ecfde3ad428fc","target_node_id":"20314099e0cd4038ad3ea5e5893412c9","branch_id":"40797b1bd5974dd388a2c06a0a251feb","id":"373ae80c013a4f63a34f77ac5e4e7a81","name":"40797b1bd5974dd388a2c06a0a251feb"},{"type":"Edge","source_node_id":"0fb3b6ff9e344abe92e34537af851c2f","target_node_id":"20314099e0cd4038ad3ea5e5893412c9","id":"ab9e0250a6b049388f0aa014af17a889"},{"type":"Edge","source_node_id":"20314099e0cd4038ad3ea5e5893412c9","target_node_id":"0c393dd87f174c9382edd8526082f120","id":"6284114b641342bb82f04ddc93e4c2b7"},{"type":"Edge","source_node_id":"0c393dd87f174c9382edd8526082f120","target_node_id":"975a0f59aa104801b0f3928fbad8ae54","branch_id":"5f0cf5ca00104d04affe6fe7115d7c95","id":"a2102245221942f4859d129905becd17","name":"5f0cf5ca00104d04affe6fe7115d7c95"},{"type":"Edge","source_node_id":"0c393dd87f174c9382edd8526082f120","target_node_id":"95d09506a92548158c