          "id": "2a3e1c9302b7463199467b476dcc5058",
          "mode": "external",
          "script": "(function execute() {\n    var options = [];\n    var index = parseInt(vaVars.index);\n    var limit = parseInt(vaVars.limit);\n    var count = 0;\n    var gr = new GlideRecordSecure('sys_user');\n    var quesJson = JSON.parse(vaVars.itemJSON).variables[vaVars.qIndex];\n    gr.addEncodedQuery('nameLIKE'+ vaInputs.search_requested_for);\n    gr.addQuery(quesJson.qualifier);\n    gr.orderBy(gr.getDisplayName());\n    gr.chooseWindow(index, index + limit, true);\n    gr.query();\n\n    var count = gr.getRowCount();\n    vaVars.count = count;\n\n    while (gr.next()) {\n        options.push({\n            \"value\" : gr.getUniqueValue(),\n            \"label\" : gr.getDisplayValue()\n        });\n    }\n\n    if (index > 0) \n        options.push({ 'value': 'prev', 'label': gs.getMessageLang('Show previous', vaContext.getRequesterLang())});\n    if (count > index + limit)\n        options.push({ 'value': 'next', 'label': gs.getMessageLang('Show more', vaContext.getRequesterLang())});\n    \n    if (count > 0) {\n        options.push({\n            'value' : 'search',\n            'label' : gs.getMessageLang('Someone else', vaContext.getRequesterLang())\n        });\n    }\n    return options;    \n})()\n",
