    vaVars.validation_message = "";
    var itemJson = JSON.parse(vaVars.itemJSON);
    var quesJson = itemJson.variables[vaVars.qIndex];
    var vaUtils = new global.VAUtils();
    var arr = vaInputs.question_attachment.getValue().split('/');
    var mediaId = arr[arr.length - 1];
    var gr = new GlideRecord('sys_cs_media');
    gr.addQuery('media_id', mediaId);
    gr.query();
    if (gr.next()) {
        var attachmentId = gr.getValue('sys_attachment_id');
        var attachRef = new GlideRecord('sys_attachment');
        if (attachRef.get(attachmentId)) {
            var file = {
                name: attachRef.getValue('file_name'),
                size: attachRef.getValue('size_bytes')
            };
            var allowedExtensions = new Array();
            if (quesJson.allow_extension)
               allowedExtensions = quesJson.allow_extension.split(';');
            var field = {
                allowedExtensions: allowedExtensions,
                allowedFileSize: quesJson.max_file_size
            };
            var result = vaUtils.validateAttachment(file, field);
            if (result.type == 'success') {
                quesJson.value = attachmentId;
                vaVars.itemJSON = JSON.stringify(itemJson);
                vaInputs.question_attachment.setValue('');
            } else
                vaVars.validation_message = result.message;
        } else 
            vaVars.validation_message = gs.getMessageLang('Failed to upload the attachment', vaContext.getRequesterLang());
    } else 
        vaVars.validation_message = gs.getMessageLang('Failed to upload the attachment', vaContext.getRequesterLang());
})()
