          "id": "2871d07959564835be9c1bad7ab741c9",
          "name": "show notifications",
          "script": "(function execute() {\n    if(vaVars.global_show_notifications_run) {\n        return;\n    }\n    if (vaSystem.isAMBClient(vaContext.deviceType)) {\n        var web_client_channel_id = 'ecc77b49533100102f40ddeeff7b125d';\n        var channelGR = new GlideRecord('sys_notification_channel');\n        channelGR.get(web_client_channel_id);\n        vaVars._notifications_enabled = (channelGR.getValue('active') === '1') ? true : false;\n    } else {\n        var channelId = JSON.parse(sn_cs.VASystemObject.getProviderByProviderAppId(vaContext.providerAppId)).channelId;\n        var cschannel = new GlideRecord('sys_cs_channel');\n        cschannel.get('sys_id',channelId);\n        vaVars._notifications_enabled = (cschannel.getValue('enable_notification') === '1') ? true : false;\n    }\n    vaVars.global_show_notifications_system_topic_trigger = true;\n    if(vaVars._notifications_enabled) {\n        var notifications = vaSystem.getPrevNotifications(true,100);\n        vaVars.global_show_notifications_pending_notifications = notifications;\n        var notificationJson = JSON.parse(notifications);\n        if(notificationJson.length > 0) {\n            if (vaVars.global_model_type === 'llm')\n                vaSystem.switchTopic('Now Assist Show Notification');\n            else\n                vaSystem.switchTopic('Show Notification');\n            \n            vaSystem.sendContextualActions();\n        }\n    }\n})()",
          "stepType": "ScriptAction",
          "applicability": {
            "mode": "script",
            "value": "(function execute() {\n    return vaSystem.getShowNotificationsPending() === 'true' && !vaSystem.isNASSConversation();\n})()\n"
