    vaVars.isSearchAvailable = global.VAAISearchHelperTokyo.isSearchEnabled(vaVars.loggingContext);

    // This script has to run before any other operations to collect correct info.
    // If the node runs under the platform thread, the info can be incorrect.
    vaVars.session_id = gs.getSessionID();
    vaVars.user_id = vaInputs.user.getValue();
    vaVars.is_logged_in = gs.getSession().isLoggedIn();
    vaVars.language = gs.getSession().getLanguage();
