          "name": "Validate User provided PIN",
          "script": "(function execute() {\n    if(vaVars.user_validation_error) {\n        vaVars.input_validation_error = true;\n        vaVars.error_code = 40000; //Invalid Soft PIN\n        return;\n    }\n    var userId = vaInputs.userid.getValue();\n    var verificationId = vaInputs.verificationid.getValue();\n    var contextId = vaInputs.contextid.getValue();\n    var hashedSoftPIN = vaInputs.softpin_input.getValue();\n    var requestId = '';\n    var requestExists = new SNC.PwdTrackingManager().requestExists(contextId); //Move to verify processor\n    if (requestExists)\n        requestId = contextId;\n\n    var userInputs = [];\n    userInputs.push({ key: 'sysparm_softpin', value: hashedSoftPIN });\n    userInputs.push({ key: 'isEncrypted', value: true });\n    userInputs.push({ key: 'request_id', value: requestId });\n    userInputs.push({ key: 'context_id', value: contextId });\n\n    var passwordResetScopedUtil = new global.PasswordResetScopedUtil();\n    var verificationResult = passwordResetScopedUtil.verify(userId, '', verificationId, requestId, userInputs);\n    if (!verificationResult) {\n        vaVars.input_validation_error = true;\n        vaVars.error_code = 40000; //Invalid Soft PIN\n        var pwdSoftpinMgr = new SNC.PwdSoftPINManager();\n        if(pwdSoftpinMgr.isPinExpirationEnabled() && pwdSoftpinMgr.isPinExpired(userId, verificationId, hashedSoftPIN, true)) {\n                vaVars.error_code = 40001; //Expired Soft PIN\n        }\n    }\n})()",
