    var userId = vaInputs.userid.getValue();
    var verificationId = vaInputs.verificationid.getValue();

    //Get question for the verification
    var passwordResetScopedUtil = new global.PasswordResetScopedUtil();
    var enrollmentMgr = new SNC.PwdEnrollmentManager();
    vaVars.softpin_question = passwordResetScopedUtil.getSoftPINQuestion(verificationId);

    //Get salt
    if(vaVars.user_validation_error) {
         vaVars.salt = passwordResetScopedUtil.getRandomSalt();
    } else {
        vaVars.salt = new SNC.PwdSoftPINManager().getSaltByUserAndVerificationId(userId, verificationId);

        if(JSUtil.nil(vaVars.salt)) { // User not enrolled
