"},"nlu_entity_binding":{"type":"NLUEntityBinding","allow_text_input":false,"use_confirmation_prompt":true},"skippable":{"type":"Applicability","mode":"script","value":"false"},"id":"26e2bda364894708acbd80938dffd97a","name":"share location?","goal_id":"primary","applicability":{"type":"Applicability","mode":"script","value":"true"}},{"type":"LibraryCall","library_topic_name":"Geolocation control","library_topic_id":"358d45bb53312010b6e8ddeeff7b1276","parameter-mappings":[],"return-value-mappings":[{"main_topic_variable_namespace":"vaInputs","library_topic_return_parameter_name":"georesponsejsonstring","main_topic_mapped_variable_name":"georesponse"}],"id":"78562d92fdcd4f418e50acaf1444c8ce","name":"Geolocation control","goal_id":"primary","applicability":{"type":"Applicability","mode":"script","value":"true"}},{"type":"ScriptedAction","script":"/*
The Geolocation API
status: ‘OK’ if location coordinates were fetched successfully, ‘ERROR` otherwise.
latitude, longitude: the geolocation coordinates
isGeoPermissionGrantedForNextUse: determines if the user has granted geolocation permission to fetch location coordinates if requested again during the conversation.
  true: do not display permission prompt since the user has granted permission on their device
  false: display permission prompt since the user either “denied permission” or used “allow once”
errorMessage: message associated with the error (optional)
errortype:
    PERMISSION_DENIED
    POSITION_UNAVAILABLE
    TIMED_OUT
    UNKOWN_ERROR
*/
(function execute() {
	if (vaInputs.share_location_ == 'false') {
		//set the appropriate error response if the user denied permission to access location
		vaVars.status = "ERROR";
		vaVars.errorType = "PERMISSION_DENIED";
		vaVars.global_is_geolocation_permission_granted = false;
		return;
	}

	if (vaInputs.georesponse) {
		const geolocationResponseObj = JSON.parse(vaInputs.georesponse);
		vaVars.status = geolocationResponseObj.status;
		if (geolocationResponseObj.status == 'OK') {
			vaVars.latitude = geolocationResponseObj.latitude;
			vaVars.longitude = geolocationResponseObj.longitude;
		} else {
			vaVars.errorType = geolocationResponseObj.errorType;
			if (geolocationResponseObj.hasOwnProperty('errorMessage')) {
				vaVars.errorMessage = geolocationResponseObj.errorMessage;
			}
		}

		if (geolocationResponseObj.hasOwnProperty('isGeoPermissionGrantedForNextUse')) {
			vaVars.global_is_geolocation_permission_granted = geolocationResponseObj.isGeoPermissionGrantedForNextUse;
		}
	}

})()","id":"94c8d0930dfd4e47be7df0e7f4aa5967","name":"Record output","goal_id":"primary","applicability":{"type":"Applicability","mode":"script","value":"true"}}],"edges":[{"type":"Edge","source_node_id":"180b3121614b4a049c13e2effa657c9c","target_node_id":"94c8d0930dfd4e47be7df0e7f4aa5967","branch_id":"6f8e8e7c00354fd1a13d6fc14ed827a1","id":"0bf55b12ae414f2797c8ad608d7398cc","name":"6f8e8e7c00354fd1a13d6fc14ed827a1"},{"type":"Edge","source_node_id":"86b7d83a7a4641bd9f38c5fb6a81ebe6","target_node_id":"57d5fe598cbb40f8b4ef8528a0472c2e","id":"89e32470e6fb41d79ac3b938fe614e47"},{"type":"Edge","source_node_id":"57d5fe598cbb40f8b4ef8528a0472c2e","target_node_id":"26e2bda364894708acbd80938dffd97a","branch_id":"53f7190dea4d45ea855856bfbfa87cda","id":"614adfc0e9be44038012ee841fdced9f","name":"53f7190dea4d45ea855856bfbfa87cda"},{"type":"Edge","source_node_id":"26e2bda364894708acbd80938dffd97a","target_node_id":"180b3121614b4a049c13e2effa657c9c","id":"2308579d79814f40a83c4e744c5867c4"},{"type":"Edge","source_node_id":"180b3121614b4a049c13e2effa657c9c","target_node_id":"78562d92fdcd4f418e50acaf1444c8ce","branch_id":"ae658c885142439aa784eb9f796a35a1","id":"6097d8fd7896411a859416428b1be595","name":"ae658c885142439aa784eb9f796a35a1"},{"type":"Edge","source_node_id":"57d5fe598cbb40f8b4ef8528a0472c2e","target_node_id":"78562d92fdcd4f418e50acaf1444c8ce","branch_id":"d1d1f100a0fa4d6896baff1f5fee98f9","id":"468e1f3a5e77410496512bb9feb191d9","name":"d1d1f100a0fa4d6896baff1f5fee98f9"},{"type":"Edge","source_node_id":"78562d92fdcd4f418e50acaf1444c8ce","target_node_id":"94c8d0930dfd4e47be7df0e7f4aa5967","id":"2ec5aaa33bd44fa4af85111589b0b8b1"},{"type":"Edge","source_node_id":"94c8d0930dfd4e47be7df0e7f4aa5967","target_node_id":"41cf7b34b57e49eca1fb8322112fccae","id":"71d56b71340f4c9b8146f75b33116380"}],"id":"primary"}],"script_variables":[{"type":"String","name":"latitude","value":{"type":"String","mode":"string"},"list":false,"id":"58a22652bd3f44a08c352740a14a847f","formatType":"text","sec