          "dataSourceType": "script",
          "script": "(function execute() {\n    var iar_config_list = JSON.parse(vaVars.iar_config_list);\n    var currConfig = iar_config_list.shift();\n    vaVars.iar_config_list = JSON.stringify(iar_config_list);\n    var grConfig = new GlideRecord(\"sys_cs_auto_resolution_configuration\");\n    grConfig.get(currConfig);\n\n    // check that configuration is active\n    var isActive = sn_oe_sfs.IARHealthCheck.checkConfigActive(currConfig) ? \"PASSED\" : \"FAILED\";\n    \n    // check that language configuration is active\n    var configLanguageActive = sn_oe_sfs.IARHealthCheck.checkConfigLanguageActive(currConfig) ? \"PASSED\" : \"FAILED\";\n\n    // check for there is an active response channel\n    var responseChannelActive = sn_oe_sfs.IARHealthCheck.checkReponseChannelActive(currConfig) ? \"PASSED\" : \"FAILED\";\n\n    // check for active intent topic map if incident\n    var incidentIntentTopicMapActive = null;\n    if (grConfig.target_table_name == \"incident\") {\n        incidentIntentTopicMapActive = sn_oe_sfs.IARHealthCheck.checkIntentTopicMapActive(currConfig) ? \"PASSED\" : \"FAILED\";\n    }\n\n    var aiSearchEnabled = null;\n    var hrSlaEngine = null;\n    if (global.AutoResolutionAPI.isHRCaseTable(grConfig.target_table_name.toString())) {\n        // check that AI search is enabled if HR case\n        aiSearchEnabled = sn_oe_sfs.IARHealthCheck.checkAISearchEnabled(currConfig) ? \"PASSED\" : \"FAILED\";\n\n        // check that \"Use SLA Engine\" field is checked if HR case\n        hrSlaEngine = sn_oe_sfs.IARHealthCheck.checkUseSLA(currConfig) ? \"PASSED\" : \"FAILED\";\n    }\n\n    var headers = [];\n    var rows = [];\n    rows.push([\n        \"Configuration is active\", isActive\n    ]);\n    rows.push([\n        \"Language configuration is active\", configLanguageActive\n    ]);\n    rows.push([\n        \"There is an active response channel\", responseChannelActive\n    ]);\n    if (incidentIntentTopicMapActive) {\n        rows.push([\n            \"There is an active intent topic map for incident configuration\", incidentIntentTopicMapActive\n        ]);\n    }\n    if (aiSearchEnabled) {\n        rows.push([\n            \"AI search is enabled for HR configuration\", aiSearchEnabled\n        ]);\n    }\n    if (hrSlaEngine) {\n        rows.push([\n            \"SLA engine is used for HR configuration\", hrSlaEngine\n        ]);\n    }\n\n    return {\n        columnHeaders: headers,\n        columnValues: rows\n    };\n})()",
