","id":"84a9b0145a364bb98d434f851e0cecfb","name":"Reroute to Flow Failure topic","goal_id":"primary","applicability":{"type":"Applicability","type":"Applicability","mode":"query","value":"reroute_confirmation=reroute"}},{"type":"ScriptedAction","type":"ScriptedAction","script":"(function execute() {
    var grConfig = new GlideRecord("sys_cs_auto_resolution_configuration");
    grConfig.get(vaVars.config_sys_id);
    var errors = JSON.parse(vaVars.errors);

    if (grConfig.iar_invocation == "api") {
        // check that if business rule exists, it is inactive
        var configBr = sn_oe_sfs.IARIssueNotAssignedDiagnostic.checkBrExists(vaVars.config_sys_id);
        if (!gs.nil(configBr) && sn_oe_sfs.IARIssueNotAssignedDiagnostic.checkBrActive(configBr)) {
            errors.push("Configuration trigger is set to on demand, but it has an active business rule");
        }
    }
    else if (grConfig.iar_invocation == "business_rule") {
        // check that business rule exists
        var configBr = sn_oe_sfs.IARIssueNotAssignedDiagnostic.checkBrExists(vaVars.config_sys_id);
        if (gs.nil(configBr)) {
            errors.push("No business rule exists for this Auto-resolution configuration");
        }

        else {
            // check that business rule is active
            if (!sn_oe_sfs.IARIssueNotAssignedDiagnostic.checkBrActive(configBr))
                errors.push("Business rule is not active");

            // check that business rule order is highest
            if (!sn_oe_sfs.IARIssueNotAssignedDiagnostic.checkBrOrderIsHighest(configBr)) 
                errors.push("Business rule order is not the highest");

            // check that there is only 1 trigger business rule associated with the configuration
            if (!sn_oe_sfs.IARIssueNotAssignedDiagnostic.checkConfigHasOneBr(configBr, vaVars.config_name)) 
                errors.push("There is more than 1 trigger business rule associated with this Auto-resolution configuration");
        }      
    }

    // check that config conditions match incident condition
    if (!sn_oe_sfs.IARIssueNotAssignedDiagnostic.checkTaskAndConfigConditionsMatch(vaVars.task_sys_id, vaVars.config_sys_id, vaVars.config_name)) {
        errors.push("Task created does not match the entry conditions on Auto-resolution configuration")
    }

    vaVars.errors = JSON.stringify(errors);
})()","id":"9b1971d1f9c94c0ea39a7cfb6e38d2fb","name":"Run checks","goal_id":"primary","applicability":{"type":"Applicability","type":"Applicability","mode":"script","value":"true"}},{"type":"TextOutputPrompt","type":"TextOutputPrompt","value":[{"type":"String","type":"String","mode":"script","value":"(function execute() {
    var errors = JSON.parse(vaVars.errors);
    if (errors.length == 0) {
        vaVars.iar_invocation_succeeded = true;
        var assignedTo = sn_oe_sfs.IARIssueNotAssignedDiagnostic.getTaskAssignedTo(vaVars.config_sys_id);
        return "After running checks, IAR invocation succeeded and the task should be assigned to " + assignedTo + ".";
    }
    else {
        var result = "After running checks, IAR invocation failed. The error(s) are as follows:\n";
        while (errors.length != 0) {
            result += "- " + errors.shift();
            if (errors.length != 0) {
                result += "\n";
            }
        }
        return result;
    }
})()"}],"id":"d84aefe43b434ee1b2f88a497433f17a","name":"Result","goal_id":"primary","applicability":{"type":"Applicability","type":"Applicability","mode":"script","value":"true"},"secure":false}],"edges":[{"type":"Edge","source_node_id":"4a0a528f521b458c8bc47d2ddbd06d0c","target_node_id":"5b4a4854f67a46ec926e8a34d10cbcb1","id":"205a7f4c590c41658fd2d69676865df9"},{"type":"Edge","source_node_id":"84a9b0145a364bb98d434f851e0cecfb","target_node_id":"d6949f2427b84faf894e7b7498997ade","id":"2399d95c7c1f4a698fbf985827d15d83"},{"type":"Edge","source_node_id":"f786e492543a46b884f92682da48c008","target_node_id":"9b1971d1f9c94c0ea39a7cfb6e38d2fb","id":"93936f321a364df588e9aec388ce396f"},{"type":"Edge","source_node_id":"9dd27ab0c12c4cb28dd16531fa58b68a","target_node_id":"eb392a3078d54509bc3c30c1aea28f8d","branch_id":"24242b8b1ea841eb9e0c3ade27da397c","id":"59609a0dbf21432e9501e5daca7dba27","name":"24242b8b1ea841eb9e0c3ade27da397c"},{"type":"Edge","source_node_id":"f550de1f7de8427a8413e37037984074","target_node_id":"9dd27ab0c12c4cb28dd16531fa58b68a","id":"cba27289643e449ab235ff563c68eab8"},{"type":"Edge","source_node_id":"5b4a4854f67a46ec926e8a34d10cbcb1","target_node_id":"f550de1f7de8427a8413e37037984074","id":"c0d0d1e6c441403d9d7fc366a8421feb"},{"type":"Edge","source_node_id":"ad4dea1e1aa944b79bb0b52c51c4ce31","target_node_id":"a341495e164e4e8f8eff85cd2d254f33","id":"99fc38cabb85476dba5b63e4ea8cb71a"},{"type":"Edge","source_node_id":"a341495e164e4e8f8eff85cd2d254f33","target_node_id":"8eaa1f219d7f43eca1bf7bb4b274ca38","branch_id":"c7d32add7d2a41f5a460214f1a3d42f3","id":"1b2f448f3d76403ab9f73bc9a3d504b5","name":"c7d32add7d2a41f5a460214f1a3d42f3"},{"type":"Edge","source_node_id":"8eaa1f219d7f43eca1bf7bb4b274ca38","target_node_id":"d6949f2427b84faf894e7b7498997ade","id":"fea155cf25e34fc382ce99ae3336e71d"},{"type":"Edge","source_node_id":"9dd27ab0c12c4cb28dd16531fa58b68a","target_node_id":"8de3469013ab4d3ab6e3c017c400c70c","branch_id":"5da0819f5b5a4c26a24b29223f22065b","id":"bf07c