    vaVars.loggingContext = JSON.stringify(global.VAAISearchHelperTokyo.getLoggingContext(vaSystem));
    global.VAAISearchHelperTokyo.getLogger(vaVars.loggingContext).info('Starting "IAR - AI Search" topic');

    // get results from api
    var contextId = JSON.parse(vaVars.global_notification_context).tableSysId;
    var aiSearchResult = global.AutoResolutionAISearchHelper.getSearchResultByContextId(contextId);

    if (aiSearchResult) {
        vaVars.ai_search_result_id = aiSearchResult.getSysId();

        // adding click metrics params
        vaVars.user_id = aiSearchResult.getUserId();
        vaVars.session_id = aiSearchResult.getSessionId();
        vaVars.is_logged_in = gs.getSession().isLoggedIn();
        vaVars.language = gs.getSession().getLanguage();

        var processedResults = global.VAAISearchHelperTokyo.processResults(aiSearchResult.getResultPayload(), vaSystem, vaInputs, vaVars);
        var formattedResults = global.AutoResolutionAISearchHelper.formatResultsForIAR(aiSearchResult, processedResults);

        vaVars.search_metadata = formattedResults.searchMetadata;
        vaVars.genius_results = formattedResults.geniusSearchResult;
        vaVars.search_results = formattedResults.searchResults;
    }

    try {
        var jsonData = JSON.parse(vaVars.genius_results);
        var catalogId = jsonData.propValues.actions[0].actionPayload.sysId.toString();
        var isCatalogSupported = new sn_sc.CatalogConversationHelper().getConversationalRenderType(catalogId, true);
        if (isCatalogSupported == "conversation")
          vaVars.catalogId = catalogId;
    } catch (e) {
        gs.info("No Catalog Id found for this search result.");
    }

})()
