          "inputType": {
            "format": "Custom",
            "script": "function validate(value) {
    // for "skip" which has input value as empty, long or percentage type, general string type with no validator defined
    if ((gs.nil(value) && vaVars.currentQuestionMandatory === 'false') || vaVars.currentQuestionType === 'long' || vaVars.currentQuestionType === 'percentage' || gs.nil(vaVars.currentQuestionFieldValidator))
        return {
            valid: true,
        }; 

    // for string type with validator defined, and question is not skipped
    var validatorGr = new GlideRecord("sys_cs_field_script_validator");
    if (!validatorGr.isValid())
	    return {
           valid: false,
           error: gs.getMessageLang("validator table does not exist", vaContext.getRequesterLang())
        };

    if (validatorGr.get(vaVars.currentQuestionFieldValidator)) {
        var scriptBuild = "(" + validatorGr.validator + ")('"+ value + "');";
        var isValidAnswer = eval(scriptBuild);
        return {
            valid: isValidAnswer,
            error: isValidAnswer ? undefined : validatorGr.error_message + ''
        };
    } else {
        return {
            valid: false,
            error: gs.getMessageLang("validator record does not exist", vaContext.getRequesterLang())
        };
    }
}"
