    var i = vaVars.questionIndex;
    var questions = JSON.parse(vaVars.surveyQuestions);
    var q = questions[i];

    var satisfied = true;
    var hasDependency = !(!q.dependsOn);
    vaVars.currentIsDependencySatisfied = !hasDependency || isSat(q);
})();

function isSat(q) {
    var questions = JSON.parse(vaVars.surveyQuestions);
    var answers = JSON.parse(vaVars.surveyAnswers);

    var answerValid = false;
    questions.forEach(function(question) {
        if (question.metric !== q.dependsOn)
            return;

        var answerExists = question.sys_id in answers;
        if (!answerExists)
            return;

        var selectedChoice = answers[question.sys_id]['value'];

        if (question.type === 'boolean' || question.type === 'checkbox') {
            if ((selectedChoice === true && q.displayedWhen === '1') || (selectedChoice === false && q.displayedWhen === '0'))
                answerValid = true;
        } else {
            var choices = question.choices.filter(function(c) {
                return c.value === selectedChoice;
            });

            if (choices.length && choices[0].sys_id)
                answerValid = q.displayedWhen.indexOf(choices[0].sys_id) !== -1;
        }
    });

    return answerValid;
}","id":"385869dc18c746b88db71bdd7c349c56","name":"Check question dependency","goal_id":"primary","applicability":{"type":"Applicability","mode":"script","value":"true"}},{"type":"InputPrompt","variable_id":"3bd6649edfb04f1d870a98c96130be34","message":{"type":"String","mode":"script","value":"(function execute() {
