    gr.query();
    while (gr.next()) {
        options.push({
            'render_style':'bubble',
            'value': gr.sys_id + '',
            'label': (isTask)? gr.number + ': ' + gr.short_description : gr.getDisplayValue()
        });
    }
    return options;
