{"type":"FlowTopic","triggers":[{"type":"Trigger","name":"bdbae08aad2347b98343aa611c7d6703","triggerType":"request","applicability":{"type":"Applicability","mode":"script","value":"true"},"id":"bdbae08aad2347b98343aa611c7d6703"}],"goals":[{"name":"primary","trigger_id":"bdbae08aad2347b98343aa611c7d6703","nodes":[{"type":"StartGoal","goal_id":"primary","greeting_msg":{"type":"String","mode":"string","value":""},"id":"b90ef5f7024d4e34b5ceaf628345df9c","name":"Start","expert_mode":false},{"type":"TerminateGoal","goal_id":"primary","confirmation_msg":{"type":"String","mode":"string","value":""},"id":"da165a652daf475698c988c6427d008f","name":"End","expert_mode":false},{"type":"ScriptedAction","script":"(function execute() {
	var gr = new GlideRecord('asmt_metric_type');
	gr.get(vaInputs.surveyid);vaVars.active = gr.active;vaVars.endNote = vaSystem.getTranslation(gr, 'end_note', vaContext.getRequesterLang(), gr.end_note.getValue() || '');vaVars.introduction = vaSystem.getTranslation(gr, 'introduction', vaContext.getRequesterLang(), gr.introduction.getValue() || '');vaVars.surveyName = gr.name.toString();vaVars.surveyVerificationMsg = vaSystem.getSurveyVerificationMessage();
})()","id":"4761ab4e62324e3cb07ce4efc77ffd1f","name":"Load survey instance","goal_id":"primary","applicability":{"type":"Applicability","mode":"script","value":"true"},"expert_mode":false},{"type":"ScriptedAction","script":"(function execute() {
    var gr = new GlideRecord('asmt_metric_category');
    gr.addQuery('metric_type', vaInputs.surveyid);
    gr.query();
    gr.next();

    var surveyCategory = gr.getUniqueValue();

    var gr = new GlideRecord('asmt_metric')
    gr.addQuery('category', surveyCategory);
    gr.addQuery('active', true);
    gr.orderBy('order');
    gr.query();

    var questions = [];
    while (gr.next()) {
        var question = {
            sys_id: gr.getUniqueValue(),
            scale: gr.scale + '',
            metric: gr.sys_id + '',
            type: gr.datatype + '',
            display: vaSystem.getTranslation(gr, 'question', vaContext.getRequesterLang(), gr.question) + '',
            dependsOn: gr.depends_on + '',
            displayedWhen: gr.displayed_when + '',
            name: gr.name + '',
            max: gr.max + '',
            min: gr.min + '',
            mandatory: gr.mandatory + '',
            field_validation: gr.field_validation + '',
            reference_table: gr.reference_table + '',
            condition: gr.condition + '',
        };

        // boolean and checkbox type questions use different displayed_when fields
        var dependsOnType = gr.depends_on.datatype + '';
        if (dependsOnType === 'boolean')
            question['displayedWhen'] = gr.displayed_when_yesno + '';
        else if (dependsOnType === 'checkbox')
            question['displayedWhen'] = gr.displayed_when_checkbox + '';

        questions.push(question);
    }
    vaVars.surveyQuestions = JSON.stringify(questions);
    vaVars.questionCount = questions.length;
})()","id":"bbe4918b84a9426abbec7d055f7f555a","name":"Load questions","goal_id":"primary","applicability":{"type":"Applicability","mode":"script","value":"true"},"expert_mode":false},{"type":"ScriptedAction","script":"(function execute() {
    vaVars.questionIndex++;
    if (!(vaVars.questionCount && vaVars.questionIndex < vaVars.questionCount))
        return;

    var i = vaVars.questionIndex;
    var questions = JSON.parse(vaVars.surveyQuestions);

    // Set question type & display value
    vaVars.currentQuestionId = questions[i].sys_id;
    vaVars.currentQuestionType = questions[i].type;
    vaVars.currentQuestionMetric = questions[i].metric;
    vaVars.currentQuestionDisplay = questions[i].display;
    vaVars.currentQuestionMax = questions[i].max;
    vaVars.currentQuestionMin = questions[i].min;
    vaVars.currentQuestionMandatory = questions[i].mandatory;
    vaVars.currentQuestionFieldValidator = questions[i].field_validation;
    vaVars.currentQuestionReferenceTable = questions[i].reference_table;
    vaVars.currentQuestionReferenceCondition = questions[i].condition;

    // Load choices if relevant
    if (questions[i].type === 'imagescale' || vaVars.choiceTypes.split(',').indexOf(questions[i].type) > -1) {
        questions[i].choices = getChoices(questions[i]);
        vaVars.surveyQuestions = JSON.stringify(questions);
    }
})();

function getChoices(q) {
    var gr = new GlideRecord('asmt_metric_definition');
    gr.addQuery('metric', q.metric);
    gr.orderBy('order');
    gr.query();

    var choices = [];
    while (gr.next()) {
        choices.push({
            sys_id: gr.getUniqueValue(),
            display: vaSystem.getTranslation(gr, 'display', vaContext.getRequesterLang(),gr.display),
            value: gr.getValue('value'),
            selected: gr.getDisplayValue('selected_image'),
            unselected: gr.getDisplayValue('unselected_image')
        });
