        sysparm_instance_id: vaVars.surveyInstanceId,
        sysparm_action: 'submit'
    };
 data.channel = 'va'; 
    var questionData = new global.TopicPreChatUtil().getQuestionData(vaVars.surveyInstanceId);

    Object.keys(answers).forEach(function(q) {
        if (answers[q] && answers[q] && answers[q].value != null){ 
            if (answers[q]['Qtype'] === 'boolean') {
                data['ASMTQUESTION:' + questionData[q].instanceID] = (answers[q]['value'] === true) ? '1' : '0';
            } else if (answers[q]['Qtype'] === 'checkbox') {
                data['ASMTQUESTION:' + questionData[q].instanceID] = (answers[q]['value'] === true) ? 'true' : '';
            } else
                data['ASMTQUESTION:' + questionData[q].instanceID] = answers[q]['value'];
            if (vaInputs.savequestionstocontext == 'true')
                saveToContext(questionData[q].name, answers[q], questionData[q].contextVar, questionData[q].type);
        }

    });
    var response = new global.AssessmentSubmitProcessor().processRequest(data);
})()

function saveToContext(name, a, contextVar, type){
        if(contextVar != "") {
            var contextVarName = contextVar;
        } else {
            //remove all special characters and replace spaces with _
            var questionName = name.toString().replace(/[^a-zA-Z0-9]/g, '_').replace(/_$/,'');     
            //append survey name to question name
            var contextVarName = "liveagent_" +vaVars.surveyName +"."+ questionName;
        }
        vaVars[contextVarName] = a.value.toString();
        //for choice questions, store response and not response index as context var
        if (type === 'imagescale' || vaVars.choiceTypes.split(',').indexOf(type) > -1) { 
             vaVars[contextVarName] = a.displayValue.toString();    
        }
