      var questions = JSON.parse(vaVars.surveyQuestions);
      var answers = JSON.parse(vaVars.surveyAnswers);
      var questionAnswerPairs = [];

      questions.forEach(function(q) {
            var question = q.display.toString();
            var answer = answers[q.metric];
            var questionAnswer = answer.displayValue != null ? answer.displayValue :
            answer.value != null ? answer.value : '';
            var questionType = q.type + '';

            if(questionType == 'checkbox' || questionType == 'boolean'){
                  if (questionAnswer == "1") {
                        questionAnswer = "Yes";
                  } else if (questionAnswer == "0") {
                        questionAnswer = "No"
                  }
            }
            if(answer){questionAnswerPairs.push({"fieldLabel": question, "fieldValue": questionAnswer});}         
      });

      var card_data = JSON.stringify({
            "title":gs.getMessageLang("Response Summary", vaContext.getRequesterLang()),
            "fields":questionAnswerPairs,
      });

      return vaSystem.renderCard("PreChat", card_data, true);
})()
