{"type":"FlowTopic","type":"FlowTopic","triggers":[{"type":"Trigger","name":"fcad5081b0cb4eeea444d1a3feb8b8c2","triggerType":"request","applicability":{"type":"Applicability","type":"Applicability","mode":"script","value":"true"},"id":"fcad5081b0cb4eeea444d1a3feb8b8c2"}],"goals":[{"name":"primary","trigger_id":"fcad5081b0cb4eeea444d1a3feb8b8c2","nodes":[{"type":"StartGoal","type":"StartGoal","goal_id":"primary","greeting_msg":{"type":"String","type":"String","mode":"string","value":""},"id":"6431e35f37a54b9890326cb59ca312c5","name":"Start"},{"type":"TerminateGoal","type":"TerminateGoal","goal_id":"primary","confirmation_msg":{"type":"String","type":"String","mode":"string","value":""},"id":"7b061ff91b0c42d59972887515332a8e","name":"End"},{"type":"InputPrompt","type":"InputPrompt","variable_id":"93ab62111e5a44168db40cb7ed1937e1","message":{"type":"String","type":"String","mode":"string","value":"gs.getMessageLang("Please input agent sys id", vaContext.getRequesterLang())"},"nlu_entity_binding":{"type":"NLUEntityBinding","allow_text_input":false,"use_confirmation_prompt":true},"skippable":{"type":"Applicability","type":"Applicability","mode":"script","value":"false"},"id":"77b764b3382247fba4efb6c683e93a71","name":"agent_id","goal_id":"primary","applicability":{"type":"Applicability","type":"Applicability","mode":"script","value":"true"},"formatType":"Text","validationScript":""},{"type":"ScriptOutputPrompt","type":"ScriptOutputPrompt","output_type":"single_part","id":"b7bdab292a074764a60c88836b7aaba1","name":"Calculate Capacity","goal_id":"primary","applicability":{"type":"Applicability","type":"Applicability","mode":"script","value":"true"},"value":{"type":"String","type":"String","mode":"string","value":"(function execute() {

    var agent_sys_id = vaInputs.agent_id;
    var final_capacity = new global.AWADiagnosticUtils().getAgentCurrentCapacity(agent_sys_id);

    var singleOutMsg = new sn_cs.SinglePartOutMsg();
    var text = "Current Capacity of Agent with SysId  " + agent_sys_id + " is " + final_capacity;
