    var CALLBACK_DECISION_SYS_ID = "05c6cc55eb9a0110ad88ca5d2352281e";
    var callbackTopicId;
    var deviceType = vaContext.deviceType;

    // Default callback flow is diabled 
    vaVars.global_callback_executed = false;

    // The callback Topic Id is being fetched form Custom Adapter Properties using Decision rules. 
    // The callback_topic_id has to be publish topic id from sys_cs_topic.
    var inputs = new Object();
    inputs["channel_id"] = vaSystem.getChannelIdByName(deviceType);
    inputs["portal"] = vaContext.portal;
    inputs["provider_application_id"] = vaContext.providerAppId;

    var decisionTable = new sn_dt.DecisionTableAPI();
    try {
        var answer = decisionTable.getDecision(CALLBACK_DECISION_SYS_ID, inputs);
        if (answer)
            callbackTopicId = answer.getValue("value");

    } catch (e) {
        gs.error("Error while running the decison rules for callback topic, " + callbackTopicId);
    }
    // Switch the custom/default callback topic
