    var providerApp = JSON.parse(sn_cs.VASystemObject.getProviderAppById(vaContext.providerAppId));
    vaVars.providerId = providerApp.providerId;
    vaVars.inboundId = providerApp.inboundId;

    var cafScriptObject = new sn_cs.CustomAdapterScriptObject();
    var accountLinkType = cafScriptObject.getAccountLinkType(vaContext.providerAppId);
    vaVars.authType = accountLinkType;

    vaVars.identifiedUser = vaContext.identifiedUser;

    vaVars.redirectToLA = sn_cs.VASystemObject.getCustomAdapterPropertyWithHierarchy(
                                    'redirect_to_live_agent_on_softpin_failure', vaContext.providerAppId) == 'true' ? true : false;

    vaVars.softPinLockoutEnabled = sn_cs.VASystemObject.getCustomAdapterPropertyWithHierarchy(
                                    'softpin_lockout_enabled', vaContext.providerAppId) == 'true' ? true : false;
    // There is a possibility that account linking flow might be aborted by the user
    // For example, when presented with a question if they want to enter soft PIN, they may say no. 
    // This output variable holds that information, so that the topic/topic block that calls this topic block
    // can make informed decisions
    vaVars.linkStatus = 'aborted';
})()
