          "script": "(function execute() {\n    vaVars.table_name = vaInputs.table;    \n\n    var title = '';\n    var subtitle = '';\n    var fields = [];\n\n    var gr = new GlideRecord(vaVars.table_name);\n    gr.addQuery('sys_id', vaVars.newRecordSysId);\n    gr.query();\n    title = gr.getClassDisplayValue();\n    if (gr.next()) {\n        subtitle = gr.getDisplayValue();\n\n        if (gr.isValidField('short_description') && gr.getValue('short_description')) {\n            fields.push({\n                \"fieldLabel\": gs.getMessageLang(gr.getElement('short_description').getLabel(), vaContext.getRequesterLang()),\n                \"fieldValue\": gr.getDisplayValue('short_description').toString()\n            });\n        } else if (gr.isValidField('description') && gr.getValue('description')) {\n            fields.push({\n                \"fieldLabel\": gs.getMessageLang(gr.getElement('description').getLabel(), vaContext.getRequesterLang()),\n                \"fieldValue\": gr.getDisplayValue('description').toString()\n            });\n        }\n\n        if (gr.isValidField('state') && gr.getValue('state')) {\n            fields.push({\n                \"fieldLabel\": gs.getMessageLang(gr.getElement('state').getLabel(), vaContext.getRequesterLang()),\n                \"fieldValue\": gr.getDisplayValue('state').toString()\n            });\n        }\n    }\n\n    var card_data = JSON.stringify({\n        \"title\": title,\n        \"subtitle\": subtitle,\n        \"fields\": fields,\n        \"table_name\": vaVars.table_name,\n        \"sys_id\": vaVars.newRecordSysId\n    });\n    return vaSystem.renderCard(\"Card\", card_data);\n})()",
