{"type":"FlowTopic","type":"FlowTopic","triggers":[{"type":"Trigger","name":"c48b1b357fc44bbc8f6191f9bdd6f1b8","triggerType":"request","applicability":{"type":"Applicability","type":"Applicability","mode":"script","value":"true"},"id":"c48b1b357fc44bbc8f6191f9bdd6f1b8"}],"goals":[{"name":"primary","trigger_id":"c48b1b357fc44bbc8f6191f9bdd6f1b8","nodes":[{"type":"StartGoal","type":"StartGoal","goal_id":"primary","greeting_msg":{"type":"String","type":"String","mode":"string","value":""},"id":"1adaad9e5bb04c48aaccaa11304f3cd7","name":"Start"},{"type":"TerminateGoal","type":"TerminateGoal","goal_id":"primary","confirmation_msg":{"type":"String","type":"String","mode":"string","value":""},"id":"9e23f6091ef4486bb56f1cff58156a99","name":"End"},{"type":"ScriptedAction","type":"ScriptedAction","script":"(function execute() {
    vaVars.sms_code_verification_mode  = 
    new global.PwdNotificationHelper().getSMSCodeVerificationMode(vaInputs.userid.getValue());
})()
","id":"f6bb3e9f748b49088ef1e046fdc18c2e","name":"Set SMS Verification Mode","goal_id":"primary","applicability":{"type":"Applicability","type":"Applicability","mode":"script","value":"true"}},{"type":"ReferenceChoiceInputPrompt","type":"ReferenceChoiceInputPrompt","mode":"external","id":"5efd81b075e34bdf958e00a59be43042","name":"SMS Providers","goal_id":"primary","applicability":{"type":"Applicability","type":"Applicability","mode":"script","value":"(function execute() {
return vaVars.sms_code_verification_mode != undefined
    && vaVars.sms_code_verification_mode.toString() == 'mobile' 
    && (new global.PasswordResetScopedUtil().isNotifyPluginEnabled() == false);
})()
"},"variable_id":"c920f4b082104c3aaec7d88470b4fb5d","message":{"type":"String","type":"String","mode":"script","value":"(function execute() {
    var message =  gs.getMessageLang("Select your mobile service provider", vaContext.getRequesterLang());
    var custom_message = '';
    if (!gs.nil(vaVars.inboundId))
        custom_message = sn_cs.VASystemObject.getBotMessage("sms_select_provider_msg", vaVars.inboundId, vaVars.providerId);

    message = gs.nil(custom_message) ? message : gs.getMessageLang(custom_message, vaContext.getRequesterLang());

