","id":"6a500c6b88684ab289ab9065ef086918","name":"Verify SMS Code and Set Response","goal_id":"primary","applicability":{"type":"Applicability","type":"Applicability","mode":"script","value":"true"}},{"type":"ScriptedAction","type":"ScriptedAction","script":"(function execute() {
    var SMS_VERIFICATION_TYPE = '58a2f398eb6001006a668c505206fed3';
    var GLIDE_CS_PLUGIN_ID = 'com.glide.cs';
    vaVars.status_code = 20000;

    var userId = vaInputs.userid.getValue();
    var verificationId =vaInputs.verificationid.getValue();
    var contextId = vaInputs.contextid.getValue();

    var pluginManager = new GlidePluginManager();
    if (pluginManager.isActive(GLIDE_CS_PLUGIN_ID)) {
        try {
            var providerJsonString = sn_cs.VASystemObject.getProviderByProviderAppId(vaContext.providerAppId);
            if (!gs.nil(providerJsonString)) {
                var providerJson = JSON.parse(providerJsonString);
                vaVars.channel = providerJson.channelType;
            }
        } catch (error) {
            gs.warn('Plugin : [com.glide.cs] is active, but failed in sn_cs.VASystemObject.getProviderByProviderAppId');
        }
    }

    //Check for valid sms verification
    var passwordResetScopedUtil = new PasswordResetScopedUtil();
    var verification = passwordResetScopedUtil.getVerificationById(verificationId);
    if (gs.nil(verificationId) ||
        !verification.exists() ||
        verification.getVerificationTypeId() != SMS_VERIFICATION_TYPE) {
        vaVars.status_code = 40003;
        return;
    }

    //Check for valid user
    var user = GlideUser.getUserByID(vaInputs.userid.getValue());
    if (gs.nil(userId) ||
        !user.exists()) {
        vaVars.status_code = 40004; 
        return;
    }

    //Check for valid context_id
    if (gs.nil(contextId)) {
        vaVars.status_code = 40002;
    }
})()
