        var arr = info.split('__');
        var build = arr[0].split('-')[1];
        var patch = '';
        if (arr.length == 2) {
            patch = arr[1].split('-')[0].slice(5);
        }
    }
    var instanceURL = gs.getProperty('glide.servlet.uri');
    var instanceName = gs.getProperty('instance_name');
    var ais_enable = gs.getProperty('glide.ais.enabled') && gs.getProperty("sn_ci_diagnostics.ais_enable", "false");
    var gr = new GlideRecord('sys_upgrade_history');
    gr.addEncodedQuery('to_versionLIKEglide-');
    gr.query();
    if(gr.next()){
       var latestUpgrade = {};
       latestUpgrade.from = gr.getValue('from_version');
       latestUpgrade.to = gr.getValue('to_version');
       latestUpgrade.dateTime = gr.getValue('upgrade_finished');
    }
    var systemInfo = {'instanceName':instanceName, 'instanceURL':instanceURL, 'build':build, 'patch':patch, 'ais_enable':ais_enable, 'latestUpgrade':latestUpgrade};
    vaVars.systemInformation = JSON.stringify(systemInfo);
})()
