    var ifRequiredPluginNotInstalled = function(pluginIds){
        if(!pluginIds || pluginIds == ''){
            return false;
        }
        var plugins = pluginIds.split(',');
        var pm = new GlidePluginManager();
        for(var i=0; i < plugins.length; i++){
            if(!pm.isActive(plugins[i]))
              return true;
        }
        return false;
    };
    var gr = new GlideRecord('ci_diagnostic_content');
    var selectionObj = JSON.parse(vaVars.selections);
    var sysids = '';
    for(var i=0; i < selectionObj.length; i++){
        if (i!=0){
           sysids = sysids + ',';
        }
        sysids = sysids +  selectionObj[i];
    }
    gr.addQuery('sys_id','IN',sysids);
    gr.query();
    var recs = {}
    var allRecords = [];
    while(gr.next()){
        var requiredPlugins = gr.getValue('plugin_dependency')
        if(ifRequiredPluginNotInstalled(requiredPlugins)){
             continue;
        }
        var obj = {};
        var category = gr.category.name;
        obj.sysId = gr.getUniqueValue();
        obj.name = gr.getValue('name');
        obj.desc = gr.getValue('description');
        obj.category = gr.getValue('category');
        obj.categoryName = gr.category.name + '';
        obj.topic = gr.support_topic.name + '';
        obj.dataCollectionTopic = gr.data_collection_topic.name + '';
        obj.healthCheckTopic = gr.category.health_check_topic.name + '';
        if (recs[category] && recs[category].length > 0 ) {
            recs[category].push(obj);
        }else {
            var arr = [];
            arr.push(obj);
            recs[category] = arr;
        }
        allRecords.push(obj);
    }
    vaVars.pluginRecords = JSON.stringify(allRecords);
    vaVars.categories = JSON.stringify(recs);
}
