    var transcript = JSON.stringify(vaSystem.getTranscript());
    var diagnosticData = {};
    diagnosticData.systemInformation = JSON.parse(vaVars.systemInformation);
    if(vaVars.global_diagnosticdata && vaVars.global_diagnosticdata != ''){
        var plugindata = JSON.parse(vaVars.global_diagnosticdata);
        var pluginName = JSON.parse(vaVars.selectedPlugin).name;
        var pluginCategory = JSON.parse(vaVars.selectedPlugin).categoryName;
        var appendData = {};
        appendData[pluginName] = plugindata;
        diagnosticData[pluginCategory] = appendData;
    }
    diagnosticData.transcript = transcript;
    var interactionSysId = vaSystem.getInteractionSysId();
    var gr = new GlideRecord('interaction');
    gr.get(interactionSysId);
    var gsa = new GlideSysAttachment();
    var id = gsa.write(gr, "diagnostic_data.json", 'text/plain', JSON.stringify(diagnosticData, null, 4));
    return '/sys_attachment.do?sys_id='+id;
})()
