        "f19fdd12474b4788b8c6dc822dceee16": {
          "id": "f19fdd12474b4788b8c6dc822dceee16",
          "name": "SoftPin Retry: Initialize",
          "script": "(function execute() {\n    var providerAppId = vaContext.providerAppId;\n    var retryInterval = sn_cs.VASystemObject.getCustomAdapterPropertyWithHierarchy('softpin_max_invalid_attempts_interval_hrs', providerAppId);\n    \n    if (vaVars.softPinLockoutEnabled)\n        vaVars.softpinRetryCount = CustomAdapterSoftPinHelper.getInvalidAttemptsForUserInInterval(providerAppId, vaContext.providerUserId, retryInterval);\n    \n    vaVars.defaultMaxSoftPinInvalidAttempts = CustomAdapterSoftPinHelper.DEFAULT_MAX_INVALID_ATTEMPTS;\n})()\n",
          "stepType": "ScriptAction",
          "applicability": {
            "value": "",
            "mode": "query"
          }
        },
        "ed061e944fb2494c99e1d18b0396c2fe": {
          "id": "ed061e944fb2494c99e1d18b0396c2fe",
          "stepType": "Decision",
          "name": "SoftPIN Lockout Enabled"
        },
        "fe8ee7a6a0cb4dadbb41fd72c90ecb09": {
          "id": "fe8ee7a6a0cb4dadbb41fd72c90ecb09",
          "name": "Record Invalid SoftPin Attempt",
          "script": "(function execute() {\n    CustomAdapterSoftPinHelper.recordInvalidAttempt(vaContext.providerAppId, vaContext.providerUserId);\n    \n    // If soft pin lockout is enabled, we will set this flag so that the calling topic knows that they\n    // should connect to LA\n    if (vaVars.softPinLockoutEnabled)\n        vaVars.connectToLA = true;\n})()\n",
          "stepType": "ScriptAction",
          "applicability": {
            "value": "",
            "mode": "query"
          }
        },
        "be8a47f5cbde4eb0b025b7a6dbbd9c22": {
          "id": "be8a47f5cbde4eb0b025b7a6dbbd9c22",
          "name": "Set Link Status to Failure",
          "script": "(function execute() {\n    vaVars.linkStatus = 'failure';\n})()\n",
          "stepType": "ScriptAction",
          "applicability": {
            "value": "",
            "mode": "query"
          }
        },
        "80675cf459854d96b0d6cc2c05625407": {
          "id": "80675cf459854d96b0d6cc2c05625407",
          "stepType": "TextOutput",
          "name": "Auth Failed",
          "secure": false,
          "value": [
            {
              "value": "(function execute() {\n    var USER_NOT_ENROLLED_CODE = \"40001\";\n    var INCORRECT_PIN_CODE = \"50000\";\n    var CONFIGURATION_ISSUE = \"40002\";\n    \n    var defaultMsg = \"Something went wrong. We’re going to transfer you to an agent. Please hold.\"; // config issue\n    var custom_message_key = \"link_account_unexpected_error_msg\";\n\n    var custom_message = sn_cs.VASystemObject.getBotMessage(custom_message_key, vaVars.inboundId, vaVars.providerId);\n\n    var prompt = custom_message || defaultMsg;\n    return gs.getMessageLang(prompt, vaContext.getRequesterLang());\n   \n})()\n",
              "mode": "script"
            }
          ],
          "applicability": {
            "value": "",
            "mode": "query"
          }
        },
        "aa6a33ff76764ef69d3474f27c0b8435": {
          "id": "aa6a33ff76764ef69d3474f27c0b8435",
          "name": "Set ConnectToLA variable",
          "script": "(function execute() {\n    gs.error(\"User : \" + vaVars.identifiedUser + \" Authentication failed : topic block: SoftPIN\" );\n    //For SMS, will not connect to live agent directly, will go through topic picker in order for Setup/Reset PIN topic block to show up\n    if (vaVars.redirectToLA)\n        vaVars.connectToLA = true;\n})()\n",
          "stepType": "ScriptAction",
          "applicability": {
            "value": "",
            "mode": "query"
          }
        },
        "c7024765d1d6426c89cfcfafedf209db": {
          "id": "c7024765d1d6426c89cfcfafedf209db",
          "stepType": "TextOutput",
          "name": "Retry Message",
          "secure": false,
          "value": [
            {
              "value": "(function execute() {\n    var defaultMsg = \"The response you provided is incorrect. Please try again.\";\n    var custom_message = sn_cs.VASystemObject.getBotMessage(\"invalid_pin_retry_msg\", vaVars.inboundId, vaVars.providerId);\n    var prompt = custom_message || defaultMsg;\n    return gs.getMessageLang(prompt, vaContext.getRequesterLang());\n})()\n",
              "mode": "script"
            }
          ],
          "applicability": {
            "value": "",
            "mode": "query"
          }
        },
        "7efce0d5daa2426f8d723bdde6031245": {
          "id": "7efce0d5daa2426f8d723bdde6031245",
          "name": "Reset SoftPIN Metadata for User",
          "script": "(function execute() {\n    \n    if (vaVars.softPinLockoutEnabled) {\n        CustomAdapterSoftPinHelper.markInvalidAttemptsInactive(vaContext.providerAppId, \n                                                                vaContext.providerUserId);\n    }\n})()\n",
          "stepType": "ScriptAction",
          "applicability": {
            "value": "",
            "mode": "query"
          }
        },
