{"type":"FlowTopic","triggers":[{"type":"Trigger","name":"bafb5d79c1c94055baaef2e048e1f966","triggerType":"request","applicability":{"type":"Applicability","mode":"script","value":"true"},"id":"bafb5d79c1c94055baaef2e048e1f966"}],"goals":[{"name":"primary","trigger_id":"bafb5d79c1c94055baaef2e048e1f966","nodes":[{"type":"StartGoal","goal_id":"primary","greeting_msg":{"type":"String","mode":"string","value":""},"id":"d2044dface7044d5ad4ce7c07639e590","name":"Start"},{"type":"TerminateGoal","goal_id":"primary","confirmation_msg":{"type":"String","mode":"string","value":""},"id":"d04c47bf1b7b42219679d037dde1f706","name":"End"},{"type":"ScriptedAction","script":"(function execute() {
    if (vaVars.softpinRetryCount == 0) {
        vaVars.softpinRetryCount = 1;
    } else {
        CustomAdapterSoftPinHelper.recordInvalidAttempt(vaContext.providerAppId, vaContext.providerUserId);
        vaVars.softpinRetryCount = vaVars.softpinRetryCount + 1;
    }
})()
","id":"cb26e271f53e4f0eaea239827dad56de","name":"SoftPin Retry : Increment retry count","goal_id":"primary","applicability":{"type":"Applicability","mode":"script","value":"true"}},{"type":"ScriptedAction","script":"(function execute() {
