","id":"1e603b3abf7c437796e94989a70c7c78","name":"set inputs","goal_id":"primary","applicability":{"type":"Applicability","mode":"script","value":"true"}},{"type":"LibraryCall","library_topic_name":"SoftPIN Verification","library_topic_id":"decd38545382011017c3ddeeff7b127f","library_topic_is_template":false,"parameter-mappings":[{"parameter-name":"userid","value":{"type":"String","mode":"string","value":"{{vaInputs.identifieduser}}"},"metadata":{"type":"String","list":false,"items":[],"use_items":false}},{"parameter-name":"verificationid","value":{"type":"String","mode":"script","value":"(function execute() {
