"},"metadata":{"type":"String","list":false,"items":[],"use_items":false}}],"return-value-mappings":[{"main_topic_variable_namespace":"vaInputs","library_topic_return_parameter_name":"status","main_topic_mapped_variable_name":"vq_status"}],"id":"eedf04585a7a481c952c8f1bcc047ff2","name":"_verification_question_topic_block","goal_id":"primary","applicability":{"type":"Applicability","mode":"script","value":"true"}},{"type":"Decision","name":"qna_userVerified","branches":[{"type":"Branch","label":"User not verified","expression":{"type":"Applicability","mode":"script","value":"(function execute() {
    var MAX_RETRY_COUNT = parseInt(sn_cs.VASystemObject.getCustomAdapterPropertyWithHierarchy("softpin_max_retry_attempts", vaContext.providerAppId)) || vaVars.defaultMaxSoftPinInvalidAttempts;
    var value = vaInputs.vq_status.getValue() == "Error"  && vaVars.softpinRetryCount>= MAX_RETRY_COUNT;

    return value;
})()
"},"id":"079f3b6755f446e2af067e910dd23a72","name":"User not verified"},{"type":"Branch","label":"Should Retry - staus_Error","expression":{"type":"Applicability","mode":"script","value":"(function execute() {
    var MAX_RETRY_COUNT = parseInt(sn_cs.VASystemObject.getCustomAdapterPropertyWithHierarchy("softpin_max_retry_attempts", vaContext.providerAppId)) || vaVars.defaultMaxSoftPinInvalidAttempts;
    var value =  vaInputs.vq_status.getValue() == "Error" && vaVars.softpinRetryCount < MAX_RETRY_COUNT;
    return value;
})()
"},"id":"8e3475c488a448adaf8d6e02936898bc","name":"Should Retry - staus_Error"},{"type":"Branch","label":"UserVerified - status_Success","expression":{"type":"Applicability","mode":"query","value":"vq_status=Success"},"id":"f5409ffb1b394fc9accde69d1a6dd013","name":"UserVerified - status_Success"}],"id":"b74b8fddb2674afa8f3a6b021ecf7d74","goal_id":"primary"},{"type":"ScriptedAction","script":"(function execute() {
    var providerAppId = vaContext.providerAppId;
    var retryInterval = sn_cs.VASystemObject.getCustomAdapterPropertyWithHierarchy('softpin_max_invalid_attempts_interval_hrs', providerAppId);

    if (vaVars.softPinLockoutEnabled)
        vaVars.softpinRetryCount = CustomAdapterSoftPinHelper.getInvalidAttemptsForUserInInterval(providerAppId, vaContext.providerUserId, retryInterval);

    vaVars.defaultMaxSoftPinInvalidAttempts = CustomAdapterSoftPinHelper.DEFAULT_MAX_INVALID_ATTEMPTS;
})()
","id":"f19fdd12474b4788b8c6dc822dceee16","name":"SoftPin Retry: Initialize","goal_id":"primary","applicability":{"type":"Applicability","mode":"script","value":"true"}},{"type":"Decision","name":"SoftPIN Lockout Enabled","branches":[{"type":"Branch","label":"Yes","expression":{"type":"Applicability","mode":"script","value":"(function execute() {
