        vaVars.is_user_valid = false; // Invalid user
        vaVars.error_code = INVALID_USER;
    }

    //Check for valid context_id
    if (gs.nil(contextId)) {
        vaVars.input_validation_error = true;
        vaVars.error_code = CONFIGURATION_ISSUE;
        return;
    }

    //Check for valid Google authenticator verification type
    var verification = passwordResetScopedUtil.getVerificationById(verificationId);
    if (gs.nil(verificationId) || !verification.exists() ||
        verification.getVerificationTypeId() != AUTHENTICATOR_VERIFICATION_TYPE) {
        vaVars.input_validation_error = true; // Invalid verification type
        vaVars.error_code = CONFIGURATION_ISSUE;
        return;
    }
