          "name": "Load questions",
          "script": "(function execute() {
    var gr = new GlideRecord('asmt_metric_category');
    gr.addQuery('metric_type', vaInputs.surveyid);
    gr.query();
    gr.next();

    var surveyCategory = gr.getUniqueValue();

    var gr = new GlideRecord('asmt_metric')
    gr.addQuery('category', surveyCategory);
    gr.addQuery('active', true);
    gr.orderBy('order');
    gr.query();

    var questions = [];
    while (gr.next()) {
        var question = {
            sys_id: gr.getUniqueValue(),
            scale: gr.scale + '',
            metric: gr.sys_id + '',
            type: gr.datatype + '',
            display: vaSystem.getTranslation(gr, 'question', vaContext.getRequesterLang(), gr.question) + '',
            dependsOn: gr.depends_on + '',
            displayedWhen: gr.displayed_when + '',
            name: gr.name + '',
            max: gr.max + '',
            min: gr.min + '',
            mandatory: gr.mandatory + '',
            field_validation: gr.field_validation + '',
            reference_table: gr.reference_table + '',
            condition: gr.condition + '',
        };

        // boolean and checkbox type questions use different displayed_when fields
        var dependsOnType = gr.depends_on.datatype + '';
        if (dependsOnType === 'boolean')
            question['displayedWhen'] = gr.displayed_when_yesno + '';
        else if (dependsOnType === 'checkbox')
            question['displayedWhen'] = gr.displayed_when_checkbox + '';

        questions.push(question);
    }
    vaVars.surveyQuestions = JSON.stringify(questions);
    vaVars.questionCount = questions.length;
})()",
