          "script": "(function execute() {
    var options = [];
    var table = vaVars.currentQuestionReferenceTable;
    var condition = vaVars.currentQuestionReferenceCondition;
    if (!table)
        return options;

    var taskExtensions = new TableUtils("task").getTableExtensions();
    var isTask = (taskExtensions.indexOf(table) > -1) ? true : false;

    var gr = new GlideRecord(table);
    if (condition)
        gr.addEncodedQuery(condition);
    gr.query();
    while (gr.next()) {
        options.push({
            'render_style':'bubble',
            'value': gr.sys_id + '',
            'label': (isTask)? gr.number + ': ' + gr.short_description : gr.getDisplayValue()
        });
    }
    return options;
})()",
