        "78562d92fdcd4f418e50acaf1444c8ce": {
          "id": "78562d92fdcd4f418e50acaf1444c8ce",
          "stepType": "CustomControl",
          "name": "Geolocation control",
          "fields": [],
          "applicability": {
            "value": "",
