          "reusableTopicChoice": "Geolocation control",
          "reusableTopicId": "358d45bb53312010b6e8ddeeff7b1276",
          "inputs": {},
          "outputs": [
            {
              "id": "a7de13939f9345688810684754b87792",
              "value": "georesponsejsonstring",
              "mapping": "georesponse"
