        "17c5346b48454f05a13f3740a53e6ff0": {
          "id": "17c5346b48454f05a13f3740a53e6ff0",
          "name": "Validate Inputs",
          "script": "(function execute() {\n    var passwordResetScopedUtil = new PasswordResetScopedUtil();\n    var AUTHENTICATOR_VERIFICATION_TYPE = 'c7835ab787773200cfab6dd207cb0b88';\n    var userId = vaInputs.userid.getValue();\n    var verificationId =vaInputs.verificationid.getValue();\n    var contextId = vaInputs.contextid.getValue();\n    var INVALID_USER = \"40000\";\n    var CONFIGURATION_ISSUE = \"40002\";\n    //Check for valid user\n    var userInfo = passwordResetScopedUtil.getUserByID(userId);\n    if (gs.nil(userId) || !userInfo.exists()) {\n        vaVars.is_user_valid = false; // Invalid user\n        vaVars.error_code = INVALID_USER;\n    }\n\n    //Check for valid context_id\n    if (gs.nil(contextId)) {\n        vaVars.input_validation_error = true;\n        vaVars.error_code = CONFIGURATION_ISSUE;\n        return;\n    }\n\n    //Check for valid Google authenticator verification type\n    var verification = passwordResetScopedUtil.getVerificationById(verificationId);\n    if (gs.nil(verificationId) || !verification.exists() ||\n        verification.getVerificationTypeId() != AUTHENTICATOR_VERIFICATION_TYPE) {\n        vaVars.input_validation_error = true; // Invalid verification type\n        vaVars.error_code = CONFIGURATION_ISSUE;\n        return;\n    }\n})()",
          "stepType": "ScriptAction",
          "applicability": {
