        "a36155f098484653a9a4968e6eb85a3c": {
          "id": "a36155f098484653a9a4968e6eb85a3c",
          "name": "Validate Authenticator Code",
          "script": "(function execute() {\n    if(!vaVars.is_user_valid) {\n        vaVars.input_validation_error = true;\n        return;\n    } else {\n        var userId = vaInputs.userid.getValue();\n        var verificationId = vaInputs.verificationid.getValue();\n        var enrollmentMgr = new SNC.PwdEnrollmentManager();\n        var USER_NOT_ENROLLED_CODE  = \"40001\";\n        var is_user_enrolled = enrollmentMgr.isUserEnrolledByVerificationId(userId, verificationId);\n        if (!is_user_enrolled) {\n            vaVars.input_validation_error = true; // User not enrolled\n            vaVars.error_code = USER_NOT_ENROLLED_CODE;\n            return; \n        }\n    }\n\n    var userId = vaInputs.userid.getValue();\n    var verificationId = vaInputs.verificationid.getValue();\n    var contextId = vaInputs.contextid.getValue();\n    var SYSPARM_OTP_CODE = 'sysparm_otp_code';\n    var INCORRECT_PIN_CODE = \"50000\";\n\n    var userInputs = [];\n    userInputs.push({ key: SYSPARM_OTP_CODE, value: vaInputs.authenticator_code.getValue()});\n\n    var passwordResetScopedUtil = new global.PasswordResetScopedUtil();\n    var verification_status = passwordResetScopedUtil.verify(userId, '', verificationId, contextId, userInputs);\n\n    if (!verification_status) {\n        vaVars.input_validation_error = true;\n        vaVars.error_code = INCORRECT_PIN_CODE;\n    }\n})()\n",
          "stepType": "ScriptAction",
          "applicability": {
