        "70cfdbc88e41480fb40aab496c953171": {
          "id": "70cfdbc88e41480fb40aab496c953171",
          "stepType": "ScriptOutput",
          "name": "Your request has been submitted. Here ar",
          "output_type": "single_part",
          "script": "(function execute() {\n\n    var tableName = vaInputs.record_table;\n    var recordId = vaInputs.record_id;\n\n    var title = '';\n    var subtitle = '';\n    var fields = [];\n    var short_description = '';\n    var gr;\n\n    if (vaVars.isTaskBasedTable)\n        gr = new GlideRecord('task');\n    else\n        gr = new GlideRecord(tableName);\n    gr.addQuery('sys_id', recordId);\n    gr.query();\n    var inputTableGR = new GlideRecord(tableName);\n    title = inputTableGR.getClassDisplayValue();\n    if (gr.next()) {\n        if (gr.isValidField('task_effective_number'))\n            subtitle = gr.getValue('task_effective_number').toString();\n        else\n            subtitle = gr.number.toString();\n\n        if (gr.isValidField('short_description')) {\n            if (vaVars.isTaskBasedTable) {\n                var checkCanRead = {checkRecordCanRead: false, checkElementCanRead: false};\n                var shortDescriptionDetails = new global.TaskTableUtils().getTaskFieldDetails(gr, \"short_description\", checkCanRead);\n                short_description = shortDescriptionDetails.displayValue;\n            } else {\n                short_description = gr.getDisplayValue('short_description').toString();\n            }\n            fields.push({\n                \"fieldLabel\": gr.getElement('short_description').getLabel(),\n                \"fieldValue\": short_description\n            });\n        }\n        if (gr.isValidField('state')) {\n            fields.push({\n                \"fieldLabel\": gr.getElement('state').getLabel(),\n                \"fieldValue\": gr.getDisplayValue('state').toString()\n            });\n        }\n    }\n\n    var card_data = JSON.stringify({\n        \"title\": title,\n        \"subtitle\": subtitle,\n        \"fields\": fields,\n        \"url\": vaVars.recordLink.toString()\n    });\n    return vaSystem.renderCard(\"Card\", card_data);\n})()\n",
