        "1c3800ef0b694affb79c55cbdc03ba00": {
          "id": "1c3800ef0b694affb79c55cbdc03ba00",
          "name": "Validate Inputs",
          "script": "(function execute() {\n    var SOFTPIN_VERIFICATION_TYPE = 'a67585845302011017c3ddeeff7b1267';\n    var userId = vaInputs.userid.getValue();\n    var verificationId = vaInputs.verificationid.getValue();\n    var contextId = vaInputs.contextid.getValue();\n\n    //Validate verification and type\n    var passwordResetScopedUtil = new PasswordResetScopedUtil();\n    var verification = passwordResetScopedUtil.getVerificationById(verificationId);\n    if (gs.nil(verificationId) ||\n        !verification.exists() ||\n        verification.getVerificationTypeId() != SOFTPIN_VERIFICATION_TYPE) {\n        vaVars.input_validation_error = true; // Not a valid verification type\n        return;\n    }\n\n    //Check for valid context_id\n    if (gs.nil(contextId)) {\n        vaVars.input_validation_error = true;\n        return;\n    }\n\n     //Check for valid user\n    var userInfo = GlideUser.getUserByID(userId);\n    if (gs.nil(userId) ||\n        !userInfo.exists()) {\n        vaVars.user_validation_error = true; // User Not Found\n    }\n\n})()\n",
          "stepType": "ScriptAction",
          "applicability": {
            "value": "",
            "mode": "query"
          }
        },
        "de09aba86bc0410ba6af149d57952e6a": {
          "id": "de09aba86bc0410ba6af149d57952e6a",
          "stepType": "Decision",
          "name": "Input Validation Check"
        },
        "bf4a8ffe77dc48fb80c288e59ef06d7b": {
          "id": "bf4a8ffe77dc48fb80c288e59ef06d7b",
          "name": "Get SoftPIN Question",
          "script": "(function execute() {\n    var userId = vaInputs.userid.getValue();\n    var verificationId = vaInputs.verificationid.getValue();\n\n    //Get question for the verification\n    var passwordResetScopedUtil = new global.PasswordResetScopedUtil();\n    var enrollmentMgr = new SNC.PwdEnrollmentManager();\n    vaVars.softpin_question = passwordResetScopedUtil.getSoftPINQuestion(verificationId);\n\n    //Get salt\n    if(vaVars.user_validation_error) {\n         vaVars.salt = passwordResetScopedUtil.getRandomSalt();\n    } else {\n        vaVars.salt = new SNC.PwdSoftPINManager().getSaltByUserAndVerificationId(userId, verificationId);\n\n        if(JSUtil.nil(vaVars.salt)) { // User not enrolled\n            vaVars.salt = passwordResetScopedUtil.getRandomSalt();\n            vaVars.user_validation_error = true; \n        }\n    }\n})()\n",
          "stepType": "ScriptAction",
          "applicability": {
            "value": "",
            "mode": "query"
          }
        },
