          "stepType": "CustomInput",
          "name": "CATALOG_ITEM_VA_CONTROL",
          "generateDataFunction": "function execute() {\n    if (gs.nil(vaInputs.requestedfor))\n        return { 'sysId': vaInputs.sysid.getValue(), 'forceCloseControl': vaInputs.forceclosecontrol.getValue(), 'outputValue': vaInputs.outputvalue.getValue(), 'catalogItemName': vaVars.catalogItemName, 'bare': true };\n    else\n        return { 'sysId': vaInputs.sysid.getValue(), 'requestedFor': vaInputs.requestedfor.getValue(), 'forceCloseControl': vaInputs.forceclosecontrol.getValue(), 'outputValue': vaInputs.outputvalue.getValue(), 'catalogItemName': vaVars.catalogItemName, 'bare': true };\n}\n",
          "messageTranscriptFunction": "function execute(controlData) {\n    return gs.getMessage('The Custom Control Catalog item VA just rendered item: {0}', controlData.catalogItemName);\n}",
          "inputResponseHandlerFunction": "function execute(controlData, responseValue) {\n    if (responseValue && responseValue.status == \"SUCCESS\" && responseValue.requestTable == \"sc_request\" && responseValue.requestId != \"\") {\n        var gr = new GlideRecord(\"sc_req_item\");\n        gr.addEncodedQuery(\"request=\" + responseValue.requestId);\n        gr.query();\n        if (gr.next()) {\n            responseValue.requestTable = \"sc_req_item\";\n            responseValue.requestId = gr.getUniqueValue();\n        }\n    }\n    vaVars.responseFromComponent = JSON.stringify(responseValue);\n}\n",
          "inputResponseMessageTranscriptFunction": "function execute(controlData, responseValue) {\n    if (responseValue.status == 'SUCCESS')\n        return gs.getMessage('Catalog item request completed');\n    else if (responseValue.status == 'CANCELED')\n        return gs.getMessage('Catalog item request cancelled');\n\n    return gs.getMessage('Status for catalog item request is: {0}', [responseValue.status]);\n}",
