        "5efd81b075e34bdf958e00a59be43042": {
          "stepType": "ReferenceChoicePrompt",
          "name": "SMS Providers",
          "prompt": {
            "value": "(function execute() {\n    var message =  gs.getMessageLang(\"Select your mobile service provider\", vaContext.getRequesterLang());\n    var custom_message = '';\n    if (!gs.nil(vaVars.inboundId))\n        custom_message = sn_cs.VASystemObject.getBotMessage(\"sms_select_provider_msg\", vaVars.inboundId, vaVars.providerId);\n    \n    message = gs.nil(custom_message) ? message : gs.getMessageLang(custom_message, vaContext.getRequesterLang());\n        \n    return message;\n})()\n",
            "mode": "script"
          },
          "confirmMessage": {
            "value": "",
            "mode": "simple"
          },
          "acknowledgeMsg": {
