        "2d3fd1b9047643b4a14ad7aa1df3e764": {
          "id": "2d3fd1b9047643b4a14ad7aa1df3e764",
          "name": "Send SMS Code",
          "script": "(function execute() {\n    var SYSPARM_SMS_VERIFICATION_MODE = 'sysparm_sms_verification_mode';\n    var MODE_MOBILE = 'mobile';\n\n    if (vaVars.sms_code_verification_mode == undefined) {\n        vaVars.status_code = 40001;\n        return;\n    }\n    \n    var providerId = '';\n    if (MODE_MOBILE == vaVars.sms_code_verification_mode.toString() &&\n        (new global.PasswordResetScopedUtil().isNotifyPluginEnabled() == false)) {\n        providerId = vaInputs.sms_providers.toString();\n    }\n\n    vaVars.device_code_status =\n        new global.PasswordResetScopedUtil().sendSMSCodeToUser(vaInputs.contextid.getValue(), vaInputs.verificationid.getValue(), vaVars.sms_code_verification_mode.toString(), providerId, vaInputs.userid.getValue());\n\n    var userInputs = [];\n    userInputs.push({\n        key: SYSPARM_SMS_VERIFICATION_MODE,\n        value: vaVars.sms_code_verification_mode.toString()\n    });\n\n    vaVars.userInputs = JSON.stringify(userInputs);\n\n    var devices = new global.PwdNotificationHelper().getSubscribedMobileDevices(vaInputs.userid.getValue(), true);\n\n    if (devices.length > 0 && devices != null) {\n        var devicesList = '';\n        for(var i =0 ; i < devices.length ; i++) {\n            devicesList = devicesList + '- ' + devices[i].device + '\\n';\n        }\n        vaVars.device_list = new global.PwdNotificationHelper().prepareMessageFromResponseCode(parseInt(vaVars.device_code_status), vaInputs.verificationid.getValue(), devicesList, vaVars.inboundId, vaVars.providerId);\n    } else {\n        var message =  gs.getMessageLang(\"Can't send verification code as you are not enrolled.\", vaContext.getRequesterLang());\n        var custom_message = '';\n        if (!gs.nil(vaVars.inboundId))\n            custom_message = sn_cs.VASystemObject.getBotMessage(\"sms_user_not_enrolled_msg\", vaVars.inboundId, vaVars.providerId);\n    \n        vaVars.device_list = gs.nil(custom_message) ? message : gs.getMessageLang(custom_message, vaContext.getRequesterLang());\n        vaVars.status_code = 40001;\n    }\n})()\n\n\n    \n        \n",
          "stepType": "ScriptAction",
          "applicability": {
            "value": "",
            "mode": "query"
          }
        },
        "d6d8f1d54c8f4985b9cae5244b34a4a9": {
