          "name": "Unable to Proceed",
          "value": [
            {
              "value": "(function execute() {\n\n    var logger = new global.AutoResolutionLoggingUtils()\n                        .withName('topic')\n                        .withContext(vaVars.loggingContext)\n                        .createLogger();\n    var message;\n\n    if (vaVars.is_notification_expired || vaVars.is_notification_accepted)\n        message =  gs.getMessageLang(\"It looks like another agent is already working on your incident. I won’t be able to help you at this time.\");\n    else\n        message =  gs.getMessageLang(\"I’m currently unable to help with your request.\");\n\n    logger.info('Unable to proceed with Auto-resolution topic, reason={0}', message);\n    return message;\n\n})()\n",
