        },
        "280229f5f0f74d11a6d06328dfb8fa1a": {
          "id": "280229f5f0f74d11a6d06328dfb8fa1a",
          "name": "Set answers",
          "script": "(function execute() {
    var answers = JSON.parse(vaVars.surveyAnswers);
    answers[vaVars.currentQuestionId] = getInput();
    vaVars.surveyAnswers = JSON.stringify(answers);
})();

function getInput() {
    var values = {'value': null, 'Qtype': vaVars.currentQuestionType, 'displayValue':null};
    if (vaVars.choiceTypes.split(',').indexOf(vaVars.currentQuestionType) > -1) {
        values.value = getValueIfSet(vaInputs.show_choice_question);
        values.displayValue = getDisplayValueIfSet(vaInputs.show_choice_question,values.value);
    }

    if (vaVars.textTypes.split(',').indexOf(vaVars.currentQuestionType) > -1) {
        values.value = getValueIfSet(vaInputs.show_text_input_question);
        values.displayValue = getDisplayValueIfSet(vaInputs.show_text_input_question,values.value);
    }

    switch (vaVars.currentQuestionType) {
        case 'imagescale':
            values.value = getValueIfSet(vaInputs.show_image_scale_question);
            values.displayValue = getDisplayValueIfSet(vaInputs.show_image_scale_question,values.value);
            var questions = JSON.parse(vaVars.surveyQuestions);
            //stores correct displayValue, vaInputs returns display+value
            if(vaInputs.show_image_scale_question && vaInputs.show_image_scale_question != null){
                questions[vaVars.questionIndex].choices.forEach(function(c){
                    if(c.value === values.value)
                        values.displayValue = c.display;
                });
            }
            break;
        case 'boolean':
            values.value = getValueIfSet(vaInputs.show_boolean_question);
            values.displayValue = getDisplayValueIfSet(vaInputs.show_boolean_question,values.value);
            break;
        case 'checkbox':
            values.value= getValueIfSet(vaInputs.show_boolean_question);
            values.displayValue = getDisplayValueIfSet(vaInputs.show_boolean_question,values.value);
            break;
        case 'attachment':
            values.value = getValueIfSet(vaInputs.show_attachment_question);
            values.displayValue = getDisplayValueIfSet(vaInputs.show_attachment_question,values.value);
            break;
        case 'date':
            const date = getValueIfSet(vaInputs.show_date_question);
            values.value = (date != null) ? date.toString() : null;
            values.displayValue = getDisplayValueIfSet(vaInputs.show_date_question,values.value);
            break;
        case 'datetime':
            const dateTime = getValueIfSet(vaInputs.show_datetime_question);
            values.value = (dateTime != null) ? dateTime.toString() : null;
            vaInputs.show_datetime_question.setTimeZone(vaContext.getDeviceTimeZone());
            values.displayValue = getDisplayValueIfSet(vaInputs.show_datetime_question,values.value);
            break;
        case 'reference':
            values.value = getValueIfSet(vaInputs.show_reference_question);
            values.displayValue = getDisplayValueIfSet(vaInputs.show_reference_question,values.value);
    }
    return values;
}

function getValueIfSet(field){
    return field && field.isValueSet() ? field.getValue() : null;
}

function getDisplayValueIfSet(field, val){
    if (!field || field == null)
       return val;
    var dv = field.getDisplayValue();
     return dv && dv.length > 0  ? dv : val;
}",
