result.applicability:
(function execute() {

    if(gs.nil(vaContext.providerAppId)){
        return false;
    }
     var providerApp = JSON.parse(sn_cs.VASystemObject.getProviderAppById(vaContext.providerAppId));
     if(gs.nil(providerApp)){
         return false;
     }

     var SMS_CHANNEL = '5ae1b3e3771700105d7b3882a91061bd';
     var providerSysId = providerApp.providerId;
     var csProviderGR = new GlideRecord("sys_cs_provider");
     csProviderGR.addQuery("sys_id", providerSysId);
     csProviderGR.addEncodedQuery("link_account_enabled=true^account_link_type!=basic");
     csProviderGR.query();

     var result = false;
     if(csProviderGR.next()){
         if(!csProviderGR.getValue("channel").equals(SMS_CHANNEL)){
            return false;
         }

         result = true;     
     }

     var session = gs.getSession();
     var loggedIn = session.isLoggedIn();
     result = result && (!loggedIn);
     return result;

})()

