    "type": "AdaptiveCard",
    "version": "1.0",
    "body": [
         {
            "type": "TextBlock",
            "text": "{{data.messageHeading}}",
          "weight":"bolder",
          "wrap": true
        },
        {
            "type": "TextBlock",
            "text": "{{data.message}}",
            "wrap": true
        },
        {
            "type": "Container",
            "separator": true,
            "items": [
                {
                    "type": "FactSet",
