gr.get(rowId);
g_uri.set('sys_id', gr.getValue('ci_sys_id'));
answer = g_uri.toString('cmdb_ci.do');
answer;

var view = g_request.getParameter('sysparm_view');
if (!view && !gs.getUser().hasRoles())
    view = 'ess';

if (view == 'ess' || view == 'checkout') {
	var checkOutForm = gs.getProperty('glide.sc.checkout.form', 'com.glideapp.servicecatalog_checkout_view');
    var useLayouts = new SNC.ServiceCatalogLayoutService().useSCLayout();
    if (useLayouts || checkOutForm == 'com.glideapp.servicecatalog_checkout_view') {
        var realID = g_uri.get('sys_id');
        g_uri.set('sysparm_sys_id', realID);
        g_uri.set('sysparm_new_request', 'false');
    }

    if (useLayouts)
        answer =  g_uri.toString('com.glideapp.servicecatalog_checkout_view_v2.do');
	else if (checkOutForm == 'com.glideapp.servicecatalog_checkout_view')
        answer = g_uri.toString('com.glideapp.servicecatalog_checkout_view.do');
}

answer;

var current_view = g_uri.get("sysparm_view");

if (current_view == "sc_ic_item_staging_design")
	g_uri.set("sysparm_view", "sc_ic_question_design");

var answer = g_uri.toString();
answer;

(function() {
	var view = g_request.getParameter('sysparm_view');
	if (!gs.nil(view) && view == "manage_security")
		return "";

    if (gs.hasRole('maint'))
		return;

  	var sysId = g_request.getParameter('sys_id');
	if (!gs.nil(sysId) && isValidSysHubAction(sysId))
		return g_uri.toString('$flow-designer.do?sysparm_nostack=true#/action-designer/' + sysId);

	return g_uri.toString('$flow-designer.do?sysparm_nostack=true#');
})();

function isValidSysHubAction(sysId) {
	var sysHubActionGr = new GlideRecord("sys_hub_action_type_definition");
	return sysHubActionGr.get(sysId);
}

var answer = g_uri.toString();
var formDefn = true;
var sys_id = g_request.getParameter('sys_id');
var instance = new GlideRecord("asmt_assessment_instance");
var view = g_request.getParameter('sysparm_view') + '';
if (instance.get(sys_id)) {
	if (g_request.getParameter('sysparm_record_list')) {
		//Check if the survey instance is complete and take the user directly to View User's Response Page
		var query = g_request.getParameter('sysparm_record_list');
		var querySplit = query.split('\\^');
		for(var i = 0; i < querySplit.length; i++) {
			if (querySplit[i].indexOf('state') === 0) {
				var instanceState = querySplit[i].substring(querySplit[i].indexOf('=') + 1);
				if (instanceState == 'complete')
					formDefn = false;
			}
		}
	} else if (sys_id) {
		//While clicking on information button for the row and open record, some parameters are not set
		if (instance.getValue('state') == 'complete')
			formDefn = false;
	}

	if ((view === 'survey' || view === 'assessment' || view === 'quiz') && !formDefn) {
		g_uri.set('sysparm_assessable_sysid', sys_id);
		g_uri.set('sysparm_assessable_type', instance.getValue('metric_type'));
		g_uri.set('sysparm_reader_view', 'true');
		g_uri.set('sysparm_instance','true');
		answer = g_uri.toString('assessment_take2.do');
	}
}
answer;

(function() {
    var sysId = g_request.getParameter('sys_id');
    if (!gs.nil(sysId)) {
     var sysHubTriggerInstanceGr = new GlideRecord("sys_hub_trigger_instance");
     if(!sysHubTriggerInstanceGr.get(sysId)){
		sysHubTriggerInstanceGr = new GlideRecord("sys_hub_trigger_instance_v2");
		sysHubTriggerInstanceGr.get(sysId);
	 }

     if (!gs.nil(sysHubTriggerInstanceGr.flow) && isValidSysHubFlow(String(sysHubTriggerInstanceGr.flow)))
         return g_uri.toString('$flow-designer.do?sysparm_nostack=true#/flow-designer/' + sysHubTriggerInstanceGr.flow);
 }

    return g_uri.toString('$flow-designer.do?sysparm_nostack=true#');
})();

function isValidSysHubFlow(sysId) {
    var sysHubFlowGr = new GlideRecord("sys_hub_flow");
    return sysHubFlowGr.get(sysId);
}

var isServiceCreatorItem = sc_ic_Factory.wrap(current).createdFromServiceCreator();
if (!isServiceCreatorItem)
	g_uri.set("sysparm_view", "sc_ic_item_staging_design");
else if (gs.hasRole("catalog_admin"))
	g_uri.set("sysparm_view", "service_admin");
else
	g_uri.set("sysparm_view", "service");

g_uri.set("sysparm_view_forced", "true");

answer = g_uri.toString(); 
answer;

(function() {
    var view = g_request.getParameter('sysparm_view');
    if (!gs.nil(view) && view == "manage_security")
        return "";

    // Convert to string
    var sysId = String(g_request.getParameter('sys_id'));
    var isValidDt = !gs.nil(sysId) && isValidDecision(sysId);
    if (isValidDt) {
        // Get the whitelisted the decision tables to open in Classic UI16 view
        var dtCsvInClassicView = gs.getProperty('com.glide.decision_table.launch_in_classic_view', '') || '';
        // Trim all the spaces in the property value
        if (dtCsvInClassicView.trim().split(/\s*,\s*/gm).indexOf(sysId) > -1) {
            return;
        }
    }

    // Ignore extraneous params
    try {
        g_uri.deleteParmameter('sysparm_target');
        g_uri.deleteParmameter('sysparm_view');
        g_uri.deleteParmameter('sysparm_list_css');
        g_uri.deleteParmameter('sysparm_group_sort');
        g_uri.deleteParmameter('sysparm_query');
        g_uri.deleteParmameter('sysparm_fixed_query');
        g_uri.deleteParmameter('sysparm_checked_items');
        g_uri.deleteParmameter('sys_id');
        g_uri.deleteParmameter('sys_is_list');
        g_uri.deleteParmameter('sys_target');
        g_uri.deleteParmameter('sysparm_referring_url');
        g_uri.deleteParmameter('sysparm_record_list');
        g_uri.deleteParmameter('sysparm_record_row');
        g_uri.deleteParmameter('sysparm_record_rows');
        g_uri.deleteParmameter('sysparm_record_target');
        g_uri.deleteParmameter('sysparm_reference');
        g_uri.deleteParmameter('sysparm_domain_restore');
        g_uri.deleteParmameter('sysparm_clear_stack');
    } catch (e) {

    }

    if (isValidDt) {
        // Existing
        return g_uri.toString('now/workflow-studio/builder?tableName=sys_decision&builderId=decision-table&sysId=' + sysId);
    } else {
        // New
        g_uri.set('action', 'insert');
        return g_uri.toString('now/workflow-studio/home/decision');
    }

})();

function isValidDecision(sysId) {
    var sysDecisionGr = new GlideRecord("sys_decision");
    return sysDecisionGr.get(sysId);
}
