(function() {
	if(new sn_kmf_ns.KMFVaultAPI().disableVaultCredCertRenewJobIfEJBCAEnabled()) {
		gs.info("Vault certificate renewal job is disabled.");
		return;
	}
	var instanceId = gs.getProperty('instance_id');
	var certCnSuffix = 'vaultcredcert';

	var nowDateTime = new GlideDateTime();

	var certGr = new GlideRecord('sys_certificate');
	certGr.addActiveQuery();
	certGr.addQuery('name', instanceId + certCnSuffix);
	certGr.orderByDesc('expires');
	certGr.query();

	// We only expect a single active sys_certificate.
	// If not, we are looking at the record with the furthest expiration date.
	if (!certGr.next()) {
		gs.info('No valid vault auth cert found. If a vault auth cert was expected, please contact CS to generate one');
		return;
	}

	var expires_in_days = GlideDateTime.subtract(nowDateTime, new GlideDateTime(certGr.getValue('expires'))).getRoundedDayPart();
	if (expires_in_days > 10) {
		gs.debug('Expiration date for the vault auth cert is over 10 days away. Skipping renewal');
		return;
	}

	// PRB1570926: Randomly schedule a renewal job, so that not all instances perform this at the same time.
	var gr = new GlideRecord("sys_trigger")
	gr.initialize();
	gr.setValue("name", "ASYNC: Renew Vault certificate");
	gr.setValue("trigger_class", "RunScriptJob");
	gr.setValue("trigger_type", 0);
	gr.setValue("job_context", "fcRunAs=system\nfcScriptName=ScriptID\\:bffd500bc30301109a21ccbfea40dd2e");

	// Find some time between 0 (now) and 12 hrs (+1 to be inclusive)
	var delay = GlideSecureRandomUtil.getSecureRandomIntBound(43201);
	var gdt = new GlideDateTime();
	gdt.addSeconds(delay);
	gr.setValue("next_action", gdt);
	gr.insert();
	gs.debug("Scheduling expiring Vault certificate renewal in " + delay + " seconds.");
})();

