(function execute() {
    var streamId1 = "snc.store_app.collector";
    var opened = AnalyticsFramework.open(streamId1);
    if (opened == 4 || opened == 0) {
        try {
            streamData();
        } finally {
            AnalyticsFramework.close(streamId1);
        }
    }

    function streamData(){
        var gr = new GlideRecord('sys_store_app');
        gr.addQuery('active', 'true');
        gr.addQuery('sys_created_on', '>=', gs.daysAgo(1));
        gr.query();
        var apps = getAppList(gr);
        //batch should be less than 2KB to  avoid throttling
        var batchSize = 20;
        var batch;
        for (var numApps = apps.length; numApps > 0; numApps -= batchSize) {
            if (numApps > batchSize)
                batch = apps.splice(0, batchSize);
            else
                batch = apps;
            obj = {};
            obj["event.type"] = "store_app_info";
            obj["apps"] = batch;
            sendBatch(obj);
        }
    }

    function sendBatch(batch) {
        answer = JSON.stringify(batch);
        var status = AnalyticsFramework.sendJSON(streamId1, answer);
        if (status != 0)
            gs.warn("Failed to send store app information with status " + status);
    }

    function getAppList(gr) {
        var apps = [];
        while(gr.next()) {
            var files = new GlideAggregate('sys_metadata');
            files.addQuery('sys_scope',gr.sys_id);
            files.addEncodedQuery("sys_update_nameSTARTSWITHsys_db_object");
            files.addAggregate('COUNT');
            files.query();
            var numTables = 0;
            if(files.next())
                 numTables = files.getAggregate('COUNT');
            var isFree = true;
            if (gr.getValue("price_type") == "paid")
                isFree = false;
            if (numTables > 0) {
                apps.push({
                    "app_name": gr.getValue("name"),
                    "is_free": isFree,
                    "n_tables": numTables
                });
            }
        }
        return apps;
    }
})();

