function schedulePrefetchingEvents() {
	var CACHE_PREFETCH_MAX_COUNTER = GlideProperties.getInt(
		"glide.analytics.cache.prefetch.max_refresh_counter", 10
	);

	if (CACHE_PREFETCH_MAX_COUNTER > 0) {
		var gr = GlideRecord('sys_analytics_cache');
		gr.addQuery('prefetch_enabled', true);
		gr.addQuery('is_queued', false);
		gr.addQuery('prefetch_refresh_counter', '<', CACHE_PREFETCH_MAX_COUNTER);
		gr.addEncodedQuery('expiration_timeRELATIVELT@minute@ahead@30');
		gr.query();
		while (gr.next()) {
			gs.eventQueue('analytics_cache.prefetch', gr, gr.key, gr.sys_created_by, 'analytics_cache_prefetch_queue');
			gr.is_queued = true;
			gr.update();
		}
	}
